            .verdierFor { internEiendomsidentifikator }

        // Kontrollen gir utslag på fritidseiendommer hvor skattyter har eienandel større enn 0, men mangler formuesverdi
        // gir ikke utslag hvis utslag på annen kontroll
        // Gir merknad i SME
        fritidseiendomUtenFormuesverdi
            .utslagFor{
                val fritidseiendomMedEierandelOgUtenFormuesverdi = hvorMinstEnForekomst { internEiendomsidentifikator.finnesBlant(eiendommer) }

                hvis(ikke(harUtslagPaaKontroll) og
                     fritidseiendomMedEierandelOgUtenFormuesverdi) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadMangel
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val skattepliktigFinnesIKontrolliste = finnesIKontrolliste(
                   "kontrolliste - Fritidseiendom utleie",
                   listeAv(partsnr))    

        // kontrollen gir utslag dersom skattyters partsnr finnes i kontrollisten
        // og det finnes grunnlag for utleie fra utleiemegler
        // skal bare gi utslag dersom skattyter ikke har lagt til mer utleieinntekt enn det som finnes i grunnlaget
        // skal ikke gi utslag dersom det finnes innsending pga sjekk mot grunnlag
        skattemelding
            .utslagFor {

            val sumGrunnlagUtleieFraUtleieformidler = forForekomsterAv{
                 grupper(
                   sjablongbehandletUtleie, 
                   sjablongbehandletUtleieIUtlandet, 
                   sjablongOgRegnskapsbehandletUtleie)}
                .filtrer { grunnlag(utleieformidler) er oppgitt }
                .summer { grunnlag(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) }

            val sumFastsattUtleieFraUtleieformidler = forForekomsterAv{
                 grupper(
                   sjablongbehandletUtleie, 
                   sjablongbehandletUtleieIUtlandet, 
                   sjablongOgRegnskapsbehandletUtleie)}
                .filtrer { fastsatt(utleieformidler) er oppgitt }
                .summer { fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) }

            val diffUtleieFraUtleiemegler =
                sumGrunnlagUtleieFraUtleieformidler - sumFastsattUtleieFraUtleieformidler


            hvis(
                ikke(harInnsending) og
                    skattepliktigFinnesIKontrolliste og
                    (sumGrunnlagUtleieFraUtleieformidler erStørreEnn 0) og
                    (diffUtleieFraUtleiemegler erStørreEnnEllerLik 0)
            )
            {
                betjeningsstrategiSettesTil(
                    ikke, merknadTips, skjulerSkatteberegning
                )
            }
        }
    }

