sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val harUtslagPaaKontroll = utslagPåKontroll("BETALT_SKATT_I_UTLANDET_MANGLER_LAND_ELLER_METODE") eller
                                   utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING")

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }                           

        val kreditfradragFormue = beregnetSkattDokument
            .summer { beregnetSkatt.skattefradrag.skattefradragForFormuesskattBetaltIUtlandet }

        val kreditfradragInntekt = beregnetSkattDokument
            .summer { beregnetSkatt.skattefradrag.skattefradragForInntektsskattBetaltIUtlandet }    

        //kontrollen gir utslag og merknad dersom oppgitt betalt skatt i utlandet på formue eller inntekt overstiger kreditfradraget for formue eller inntekt som skattyter får
        //og det er registrert minst et objektkort med samme land og dobbeltbeskatningsmetode = kredit
        //skal ikke gi utslag hvis utslag på annen kontroll
        //skal ikke gi utslag hvis skattyter har begrenset skatteplikt
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .utslagForFørste {

                val landMedUtenlandskSkatt = verdierFor { landkode }

                val betaltSkattIUtlandetFormue =
                    summer { kravOmKreditfradragForUtenlandskFormuesskatt.beloepSomTall }

                val betaltSkattIUtlandetInntekt =
                    summer { kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall }

                val eiendommerIUtlandetMedBetaltSkatt = skattemeldingUtgått
                    .forForekomsterAv(fastEiendom)
                    .filtrer { fastsatt(landkode).finnesBlant(landMedUtenlandskSkatt) }
                    .verdierFor { internEiendomsidentifikator }

                val harEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            realisasjonAvFastEiendom,
                            sjablongbehandletUtleieIUtlandet,
                            regnskapsbehandletUtleieIUtlandet,
                            formuesgrunnlagSelveidBolig,
                            formuesgrunnlagBoenhetIBoligselskap,
                            formuesgrunnlagFlerboligbygning,
                            formuesgrunnlagTomt,
                            formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                            formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                            formuesgrunnlagBorett,
                            formuesgrunnlagEgenFritaksbehandletBolig,
                            formuesgrunnlagRegnskapsbehandletBolig,
                            formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                            formuesgrunnlagRegnskapsbehandletFritidseiendom,
                            formuesgrunnlagSelveidFritidseiendom
                        )
                    )
                    .filtrer {
                        fastsatt(internEiendomsidentifikator).finnesBlant(
                            eiendommerIUtlandetMedBetaltSkatt
                        )
                    }
                    .forAlleForekomster
                    .hvorMinstEnForekomst { fastsatt(metodeVedDobbeltbeskatning) erLik "kredit" }

                val harAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt = skattemeldingUtgått
                    .forForekomsterAv(
                        gruppe(
                            arbeidsgiver,
                            pensjonsinntekt,
                            konto,
                            aksjeIkkeIAksjonaerregisteret,
                            aksjeIAksjonaerregisteret,
                            verdipapirfond,
                            obligasjonOgSertifikat,
                            aksjesparekonto,
                            annetFinansprodukt,
                            skattefavorisertIndividuellSparingTilPensjon,
                            annenKapitalinntekt,
                            skattepliktigKundeutbytteUtenforVirksomhetPerUtbetaler,
                            virtuellEiendel,
                            deltakersAndelAvFormueOgInntekt
                        )
                    )
                    .filtrer { fastsatt(landkode).finnesBlant(landMedUtenlandskSkatt) }
                    .forAlleForekomster
                    .hvorMinstEnForekomst {
                        fastsatt(metodeVedDobbeltbeskatning) erLik "kredit"
                    }

                val harGrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil =
                    skattemeldingUtgått
                        .forForekomsterAv(grunnlagForKreditfradragFraNaeringsvirksomhetIUtlandet)
                        .hvorMinstEnForekomst { fastsatt(landkode).finnesBlant(landMedUtenlandskSkatt) }


                hvis(ikke(harBegrensetSkatteplikt) og
                    ikke(harUtslagPaaKontroll) og
                        ((betaltSkattIUtlandetFormue erStørreEnn kreditfradragFormue) eller (betaltSkattIUtlandetInntekt erStørreEnn kreditfradragInntekt)) og
                        (harEiendomskortMedLandOgMetodeVedDobbeltbeskatningOppgitt eller
                            harAndreKortMedLandOgMetodeVedDobbeltbeskatningOppgitt eller
                            harGrunnlagForKreditfradragFraNaeringsvirksomhetIUtlandetMedLandDetErBetaltSkattTil)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val skattPaaBruttoAksjeutbytte = utgåtteSkattemeldingFelter {
           skattemeldingUtgått
           .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
           .summer { skattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall }
       }

        // kontrollen gir utslag og merknad dersom kreditfradraget for inntektsåret er avkortet for aksjeutbytte
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditfradragSumFormueOgInntekt)
            .filtrer { fastsatt(avkortetSkattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap) erStørreEnn 0 }
            .utslagForFørste {

                val differanseAvkortetSkattPaaAksjeutbytte =
                    skattPaaBruttoAksjeutbytte - avkortetSkattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap

                hvis(differanseAvkortetSkattPaaAksjeutbytte erStørreEnn 0) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val differanseMellomInnrapportertOgFastsattSjoemannsfradrag = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(saerskiltFradragForSjoefolk)
            .hvorMinstEnForekomst {
                (fastsatt(fradragsberettigetBeloep.beloepSomTall) - grunnlag(fradragsberettigetBeloep.beloepSomTall)) erStørreEnn 0
            }
        }

        // Kontrollen gir utslag dersom skattyter har økt særskilt fradrag for sjøfolk
        // Dette gjøres ved å øke lønn i lønnskortet og merke lønnen med girRettTilSaerskiltFradragForSjoefolk- merknaden skal derfor vises der
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .filtrer { fastsatt(girRettTilSaerskiltFradragForSjoefolk) erLik true }
            .utslagFor {

                val harEndretLønnSomGirRettTilSaerfradragForSjoefolk =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (fastsatt(beloepSomGruppe.beloepSomTall) - grunnlag(beloepSomGruppe.beloepSomTall)) erStørreEnn 0
                        }

                hvis(harEndretLønnSomGirRettTilSaerfradragForSjoefolk og differanseMellomInnrapportertOgFastsattSjoemannsfradrag) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
        }
    }

