sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harOppgittTrygdefriLoenn = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .filtrer { fastsatt(saerskiltSkatteplikt) erLik "skattepliktEtterPetroleumsskatteloven" }
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .hvorMinstEnForekomst {
                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForHelsedel") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriUtenTrygdeavgiftsplikt") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForHelsedel ") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForPensjonsdel ") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada  ") eller
                    (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftsplikt")
            }
        }

        // kontrollen gir utslag dersom skattyter har oppgitt tilleggsinformasjon under andre forhold for petroleumsskatteloven
        // (har svar Ja eller Delvis på spørsmål om lønnen er opptjent om bord på utenlandsk skip i fart på norsk sokkel)
        // gir bare utslag dersom skattyter ikke har oppgitt trygdemessig unntak på lønn som er merket med særskilt skatteplikt for petroleumskatteloven
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(tilleggsinformasjonVedSaerskiltSkattepliktEtterPetroleumsskatteloven)
            .utslagFor {
                val harOppgittTilleggsinformasjon = hvorMinstEnForekomst {
                    arbeiderPaaUtenlandskRegistrertSkipIFartPaaNorskSokkel.finnesBlant(
                        "ja",
                        "delvis"
                    )
                }

                hvis(harOppgittTilleggsinformasjon og ikke(harOppgittTrygdefriLoenn)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

