sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val defaultSluttPendlerboligNaarIkkeOppgitt = "2023-12-31".dato
      val defaultSluttArbeidsstedNaarIkkeOppgitt = "2023-12-31".dato

      // kontrollen gir utslag og merknad dersom skattyter ikke har arbeidssted i samme periode som oppgitt pendlerbolig
      // må også hensynta dersom skattyter ikke har oppgitt sluttdato for arbeidssted og/eller pendlerbolig
      skattemelding
      .fokusområde("Fokusområde")
        .forForekomsterAv { pendleropphold }
        .forForekomsterAv { spesifikasjonAvPendlerbolig }
        .utslagFor {

          val startPendlerbolig = verdiFor { startdato }
          val sluttPendlerbolig = verdiForEllerDefault(defaultSluttPendlerboligNaarIkkeOppgitt) { sluttdato }

          val startDatoErOppgitt =
          hvorMinstEnForekomst { startdato er oppgitt }

          val harIkkeArbeidsstedISammePeriode = skattemelding
          .forForekomsterAv { pendleropphold }
            .forForekomsterAv { spesifikasjonAvArbeidssted }
            .harIngenForekomsterDer { forekomst ->
              val startArbeidssted = forekomst.verdiFor { startdato }
              val sluttArbeidssted = forekomst.verdiForEllerDefault(defaultSluttArbeidsstedNaarIkkeOppgitt) { sluttdato } 
              val harIkkeArbeidsstedISammePeriodeSomPendlerboligMedSluttdatoArbeidsstedOgSluttdatoPendlerbolig = 
              ((startPendlerbolig erEtterEllerLik startArbeidssted) og (startArbeidssted erFørEllerLik sluttPendlerbolig)) og
              ((sluttArbeidssted erEtterEllerLik startPendlerbolig) og (sluttPendlerbolig erFørEllerLik  sluttArbeidssted))

              harIkkeArbeidsstedISammePeriodeSomPendlerboligMedSluttdatoArbeidsstedOgSluttdatoPendlerbolig
            }

          hvis( startDatoErOppgitt og harIkkeArbeidsstedISammePeriode) {
            betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(gruppe(oppholdUnderYrkesOgTjenestereise, oppholdUnderKjoereoppdrag))
            .summer { antallOvernattingsdoegn }
        }

        val antallOvernattingsdøgnPendleropphold = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(pendleropphold, spesifikasjonAvPendlerbolig))
            .summer { antallOvernattingsdoegn }
        }

        //kontrollen gir utslag dersom skattyter har merkostnader for mer enn 365 overnattingsdøgn
        //Gir merknadStandard
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    sti(pendleropphold, spesifikasjonAvPendlerbolig),
                    oppholdUnderYrkesOgTjenestereise,
                    oppholdUnderKjoereoppdrag
                )
            )
            .utslagFor {
                val harAntallReiser = hvorMinstEnForekomst { antallOvernattingsdoegn er oppgitt }

                hvis(
                    harAntallReiser og
                        ((antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag + antallOvernattingsdøgnPendleropphold) erStørreEnn 365)
                )

                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val pendleroppholdene = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(
            sti(
                pendleropphold,
                spesifikasjonAvPendlerbolig
            )
        )
        }

        val overlappendePendleropphold = utgåtteSkattemeldingFelter {
            pendleroppholdene.hvor {
                val start = verdiFor { startdato }.dato
                val slutt = verdiFor { sluttdato }.dato
                val overlapperMedAndreOpphold = pendleroppholdene.filtrer {
                    val start2 = verdiFor { startdato }.dato
                    val slutt2 = verdiFor { sluttdato }.dato
                    (
                        (start2 erStørreEnnEllerLik start) og (start2 erMindreEnn slutt)
                        ) eller (
                        (slutt2 erStørreEnn start) og (slutt2 erMindreEnnEllerLik slutt)
                        )
                }
                overlapperMedAndreOpphold.antall erStørreEnn 1
            }
        }

        // kontrollen gir utslag dersom det er registrert flere pendleropphold på samme periode
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(pendleropphold)
            .utslagFor {
                val harPendleropphold = forForekomsterAv(spesifikasjonAvPendlerbolig)
                    .hvorMinstEnForekomst { fastsatt(startdato) er oppgitt }

                hvis(harPendleropphold og overlappendePendleropphold) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        //har godtgjørelse fra arbeidsgiver
        val godtgjoerelseFraarbeidsgiver = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.forForekomsterAv(arbeidsgiver)
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .hvorMinstEnForekomst {
                behandlingsart.finnesBlant(
                    "BOLIG_UTL_PENDLER", "DIETT_HOTELL",
                    "DIETT_LANGTR_UTL", "DIETT_HYBEL",
                    "DIETT_PENSJONAT", "FRI_KO_LO_BO_PENDLER",
                    "FRI_KOST_LOSJI", "GODTGJ_EOS_PENDLER",
                    "KOSTBESPARELSE", "LOSJI_BRAKKE",
                    "TFRI_KOST_BESOK", "TFRI_KOST_O_28DG",
                    "TFRI_KOST_O_28DG_UT", "TFRI_KOST_TJENESTE",
                    "UTGGJ_REISE_KOST_LOSJI"
                )
            }
        }

        //kontrollen gir utslag og merknad dersom skattyter har godtgjørelser fra arbeidsgiver
        //og har merkostnad hvor antall døgn er større enn 0
        //og det ikke er oppgitt at merkostnad er dekket av arbeidsgiver
        //merknaden skal kun vises på merkostnadskortet og ikke på lønnskortene
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
                .fokusområde("Fokusområde")
                .forForekomsterAv(
                    gruppe(
                        pendleropphold,
                        oppholdUnderKjoereoppdrag,
                        oppholdUnderYrkesOgTjenestereise
                    )
                )
                .utslagFor {
                    //Har ingen merkostnader av type oppholdUnderYrkesOgTjenestereise eller oppholdUnderKjoereoppdrag som er oppgitt med lavere sats
                    val merkostnaderUtenGodtgjørelseLavereEnnSats = hvorMinstEnForekomst {
                        (fastsatt(antallOvernattingsdoegn) erStørreEnn 0) og
                            (fastsatt(mottarKostgodtgjoerelseLavereEnnSats) erLik false)
                    }

                    //har ingen merkostnad av type pendleropphold med godtgjørelse fra arbeidsgiver
                    val merkostnaderUtenOppgittDekketAvArbeidsgiver =
                        forForekomsterAv(spesifikasjonAvPendlerbolig)
                            .hvorMinstEnForekomst {
                                (fastsatt(antallOvernattingsdoegn) erStørreEnn 0) og
                                    (fastsatt(utbetaltKostgodtgjoerelseFraArbeidsgiver.beloepSomTall) ikkeEr oppgitt)
                            }

                    hvis(
                        (merkostnaderUtenOppgittDekketAvArbeidsgiver eller merkostnaderUtenGodtgjørelseLavereEnnSats) og
                            godtgjoerelseFraarbeidsgiver
                    )

                    {
                        betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                    }
                }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val mottokFesteavgiftIFjor = fjoraaretsSkattemelding
         .forForekomsterAv(inntektsaar2022.kapitalisertFesteavgift)
         .hvorMinstEnForekomst{fastsatt(inntektsaar2022.mottattFesteavgift.beloepSomTall) erStørreEnn 0}


        //kontrollen gir utslag dersom skattyter i fjor mottok festavgift,
        //men ikke har fått preutfylt eller egenfastsatt dette i år
        //kontrollen gir tips på kort for festavgift
        skattemelding
            .utslagFor {

            val harMottattFestavgiftIAar = forForekomsterAv{kapitalisertFesteavgift}
                .hvorMinstEnForekomst { fastsatt(mottattFesteavgift.beloep) erStørreEnn 0 }

            hvis(mottokFesteavgiftIFjor og ikke(harMottattFestavgiftIAar))
            {
                betjeningsstrategiSettesTil(ikke, merknadTips)
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val kontrollisteOppslag = hentKontrolliste(
            "kontrolliste - Medlemskap folketrygden",
            listeAv(partsnr))
            .forForekomsterAv { verdi }

        val alleVerdierFraKontrolliste = kontrollisteOppslag
            .alleVerdier

        val verdiDekningFraKontrolliste = kontrollisteOppslag
            .verdierForListe { tekstVerdier("verdi8", "verdi9", "verdi10", "verdi11", "verdi12", "verdi13") }

        //kontrollen gir utslag og merknad dersom skattyters partsnr finnes i kontrollisten
        //og de i kontrollisten har dekning = FTL_2-9_1_ledd_a/FTL_2-9_2_ld_jfr_1a/FTL_2-7a_2_ledd_b/FTL_2-7_3_ledd_b
        //(Nav Internasjonalt har fattet et vedtak om frivillig/pliktig medlemskap i norsk folketrygd helsedel)
        //og skattyter IKKE har registrert trygdefritak for pensjonsdel på utenlandsk lønn/næringsinntekt
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, naeringsinntektMv))
            .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
            .utslagForFørste {

                val harLoenn = hvorMinstEnForekomst { arbeidsgiversNavn er oppgitt }

                val harNaering = hvorMinstEnForekomst { naeringstype er oppgitt }

                val harRegistrertTrygdefritakPensjonsdelLoenn =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt) og
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                                )
                                    )
                        }

                val harRegistrertTrygdefritakPensjonsdelNaeringsinntekt = hvorMinstEnForekomst {
                    (fastsatt(naeringsinntektTilordnetInnehaver.beloepSomTall) er oppgitt) og
                        (fastsatt(naeringsinntektTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                        )
                            )
                }

                val harRegistrertTrygdefritakPensjonsdelNaeringsunderskudd = hvorMinstEnForekomst {
                    (fastsatt(underskuddTilordnetInnehaver.beloepSomTall) er oppgitt) og
                        (fastsatt(underskuddTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                        )
                            )
                }

                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant(
                        "FTL_2-9_1_ledd_a",
                        "FTL_2-9_2_ld_jfr_1a",
                        "FTL_2-7a_2_ledd_b",
                        "FTL_2-7_3_ledd_b"
                    )) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        ((harLoenn og ikke(harRegistrertTrygdefritakPensjonsdelLoenn)) eller
                            (harNaering og ikke(harRegistrertTrygdefritakPensjonsdelNaeringsinntekt) og
                                ikke(harRegistrertTrygdefritakPensjonsdelNaeringsunderskudd)))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadITopp
                    )
                }
            }
        }
    }

