            .utslagFor {
                hvis((verdiFor { antallMaanederDrevetIAar } erStørreEnn 12) eller (verdiFor { antallMaanederDrevetIAar } erMindreEnn 0)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
