    .hvis({ naeringsspesifikasjon.harInnhold }) {
        // kontrollen gir utslag når gjennomsnittsverdi av inngående og utgående verdi for foretaksgjeld under spesifikasjonAvSkjermingsgrunnlag er negativ
        // kontrollen gir merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv{fordeltBeregnetPersoninntekt.spesifikasjonAvSkjermingsgrunnlag}
            .utslagFor {
                val inngåendeVerdiForetaksgjeld =
                    filtrer { fastsatt(skjermingsgrunnlagstype) erLik "foretaksgjeld" }
                        .summer { inngaaendeVerdi.beloep }

                val utgåendeVerdiForetaksgjeld =
                    filtrer { fastsatt(skjermingsgrunnlagstype) erLik "foretaksgjeld" }
                        .summer { utgaaendeVerdi.beloep }

                val gjennomsnittsverdiForetaksgjeld =
                    (inngåendeVerdiForetaksgjeld + utgåendeVerdiForetaksgjeld) / 2

                hvis(gjennomsnittsverdiForetaksgjeld erMindreEnn 0) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                        Avviksbeloep settesTil gjennomsnittsverdiForetaksgjeld
                    )
                }
            }
    }

