        val harFeilSkattekostnadTypeForIngenRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.skattekostnad }
            .forForekomsterAv { kostnad }
            .hvorMinstEnForekomst {
                type.finnesBlant("8300", "8321", "8322", "8323", "8324",
                                 "7.80.0.10", "7.80.0.10.00", "7.80.7.89", "7.80.7.89.00", "8.91.7.89.00",
                                 "8.91.7.89", "8.92.7.89.00", "8.92.7.89")
            }

        val harFullRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst { regnskapspliktstype erLik "fullRegnskapsplikt"}

        val harFeilSkattekostnadTypeForFullRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.skattekostnad }
            .forForekomsterAv { kostnad }
            .hvorMinstEnForekomst {
                type.finnesBlant( "7.80.0.10", "7.80.0.10.00", "7.80.7.89", "7.80.7.89.00", "8.91.7.89.00",
                                 "8.91.7.89", "8.92.7.89.00", "8.92.7.89")
            }

        // kontrollen gir utslag dersom skattepliktig kominderer en regnskapsplikttype og type skattekostnad som ikke er gyldig
        naeringsspesifikasjon.utslagFor {
            hvis(
                harGyldigVirksomhetstype og
                    ((harIngenRegnskapspliktEllerBegrensetRegnskapsplikt og
                        harFeilSkattekostnadTypeForIngenRegnskapspliktEllerBegrensetRegnskapsplikt) eller
                        (harFullRegnskapsplikt og harFeilSkattekostnadTypeForFullRegnskapsplikt))
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    faktiskFeil,
                    1,
                    Gruppering settesTil Næring
                )
            }
        }
    }

