            .forForekomsterAv { skatteplikt }
            .verdiFor { fastsattEllerGrunnlag(partsnummer) }

        val harIngenRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst { regnskapspliktstype.finnesBlant("ingenRegnskapsplikt", "begrensetRegnskapsplikt") }

        // Kontrollen gir utslag dersom skattyter har oppgitt kostnader til elektroniske kommunikasjonstjenester på konto 6995, men som ikke har tilbakeført for privat bruk på konto 6998
        // AB-test på skattytere som har tallene 0, 2, 4, 6 eller 8 i 7.siffer i partsnummeret
        // Gir merknad
        naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.driftskostnad.annenDriftskostnad }
            .utslagFor {

                val harElektroniskKommunkasjon = forForekomsterAv { kostnad }
                    .filtrer { fastsatt(type) erLik "6995" }
                    .summer { beloep.beloep }

                val harTilbakeførtElKomm = forForekomsterAv { kostnad }
                    .filtrer { fastsatt(type) erLik "6998" }
                    .summer { beloep.beloep }

                hvis(
                    harIngenRegnskapspliktEllerBegrensetRegnskapsplikt og (harElektroniskKommunkasjon erStørreEnnEllerLik 5000) og (
                        ikke(harTilbakeførtElKomm erStørreEnn 0)) og (partsnr.tegnFraIndeks(7).finnesBlant("0","2","4","6","8"))
                ) {
