      val orgnrSkattepliktig = skattepliktUpersonligGjeldende
         .forForekomsterAv {opplysningOmSkattesubjekt} 
         .verdiFor{organisasjonsnummer}

        // Gir utslag hvis innsending gjelder upersonlig skattepliktig
        // OG skatteplikten er omfattet av særregler for havbruk
        // OG selskapetsAndelAvMaksimalBiomasseAvvikerFraAndelAvBunnfradraget
        //     ((sumBunnfradragEgetSelskap / 54_600_000 * 100) - sumAndelAvMaksimalTillattBiomasseEgetSelskap)  erStørreEnnEllerLik 2
       skattemeldingUpersonlig
          .utslagFor {

            val sumBunnfradragEgetSelskap = forForekomsterAv{havbruksvirksomhet}
               .forForekomsterAv{beregnetGrunnrenteskatt.forholdSomPaavirkerGrunnrenteskattKnyttetTilSelskapISammeKonsern}
               .filtrer{organisasjonsnummer erLik orgnrSkattepliktig}
               .summer{bunnfradrag}

            val sumAndelAvMaksimalTillattBiomasseEgetSelskap = forForekomsterAv{havbruksvirksomhet}
               .forForekomsterAv{beregnetGrunnrenteskatt.forholdSomPaavirkerGrunnrenteskattKnyttetTilSelskapISammeKonsern}
               .filtrer{organisasjonsnummer erLik orgnrSkattepliktig}
               .summer{andelAvMaksimalTillattBiomasse}   

            val selskapetsAndelAvMaksimalBiomasseAvvikerFraAndelAvBunnfradraget = hvorMinstEnForekomst{
              ((sumBunnfradragEgetSelskap / 54_600_000 * 100) - sumAndelAvMaksimalTillattBiomasseEgetSelskap)  erStørreEnnEllerLik 2} 

          hvis(harSkattepliktHavbruk og
               selskapetsAndelAvMaksimalBiomasseAvvikerFraAndelAvBunnfradraget)
