        // Gir utslag hvis innsending gjelder upersonlig skattepliktig
        // OG skatteplikten er omfattet av særregler for havbruk
        // OG Selskapet har oppgitt positivGrunnrenteinntektFoerSamordning i skattemelding større enn 0 og den er ulik grunnrenteinntektFoerSamordning i NS
        //     ELLER har oppgitt negativGrunnrenteinntektFoerSamordning i skattemelding større enn 0 og den er ulik grunnrenteinntektFoerSamordning * -1 i NS
        // når avviket er større enn 100
       skattemeldingUpersonlig
          .utslagFor {

            val sumPositivGrunnrenteinntektFoerSamordning = forForekomsterAv{havbruksvirksomhet}
               .filtrer{beregnetGrunnrenteskatt.positivGrunnrenteinntektFoerSamordning erStørreEnn 0}
               .summer{beregnetGrunnrenteskatt.positivGrunnrenteinntektFoerSamordning}

            val sumNegativGrunnrenteinntektFoerSamordning = forForekomsterAv{havbruksvirksomhet}
               .filtrer{beregnetGrunnrenteskatt.negativGrunnrenteinntektFoerSamordning erStørreEnn 0}
               .summer{beregnetGrunnrenteskatt.negativGrunnrenteinntektFoerSamordning}

            val differansePositivGrunnrenteinntektErUlikSumGrunnrenteinntekt =
                differanse (sumPositivGrunnrenteinntektFoerSamordning, sumGrunnrenteinntektFoerSamordning)

            val differanseNegativGrunnrenteinntektErUlikNegativsumGrunnrenteinntekt =
                differanse (sumNegativGrunnrenteinntektFoerSamordning, negativsumGrunnrenteinntektFoerSamordning)

          hvis(harSkattepliktHavbruk og
              (((sumPositivGrunnrenteinntektFoerSamordning erStørreEnn 0) og (differansePositivGrunnrenteinntektErUlikSumGrunnrenteinntekt erStørreEnn 100)) eller
               ((sumNegativGrunnrenteinntektFoerSamordning erStørreEnn 0) og (differanseNegativGrunnrenteinntektErUlikNegativsumGrunnrenteinntekt erStørreEnn 100)))
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

