sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) {
        //kontrollen gir utslag dersom innrapportert næringsinntekt er større enn sum driftsinntekt i resultatregnskapet
        // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
     //kontrollen gir merknad

      	val partsnr = skattepliktGjeldende
            	.forForekomsterAv { skatteplikt }
            	.verdiFor { fastsattEllerGrunnlag(partsnummer) }

          val driftsinntekterSummert = naeringsspesifikasjon
             	.summer { resultatregnskap.driftsinntekt.sumDriftsinntekt.beloep }

          val driftsinntekterSomUnntas = naeringsspesifikasjon
      		.forForekomsterAv { resultatregnskap.driftsinntekt.annenDriftsinntekt }
             	.forForekomsterAv { inntekt }
             	.filtrer { (type).finnesBlant("3890", "3895", "3910") }
             	.summer { beloep.beloep }

         	val sumInnrapportertNæringsinntekt = utgåtteSkattemeldingFelter {
                skattemeldingUtgått
                .forForekomsterAv(oppgavegiver)
                .summerIkkeInkluderForekomster { inntektFraUtbetalingTilSelvstendigNaeringsdrivendeOverGrenseverdi }
            }

          val avvikBeløp = sumInnrapportertNæringsinntekt - driftsinntekterSummert + driftsinntekterSomUnntas      

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(naeringsinntektMv)
            .utslagFor {

                hvis(
                    (sumInnrapportertNæringsinntekt erStørreEnn 0) og (naeringstype er oppgitt) og (avvikBeløp erStørreEnnEllerLik 1000) og
                        (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8"))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        Avviksbeloep settesTil avvikBeløp
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .hvis({ naeringsspesifikasjon.harInnhold }) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor {fastsattEllerGrunnlag(partsnummer) }

        //kontrollen gir utslag dersom skattepliktig skattepliktig står på kontrolliste over tømmeromsetning og ikke rapporterer salgsinntekt i temaet Skog og tømmerkonto
        //kontrollen gir validering i SME

      	val erLeveringspliktig = opplysningerOmSkattesubjektGjeldendeDokument
            .forForekomsterAv{opplysningerOmSkattesubjekt}
            .hvorMinstEnForekomst{leveringsplikt.harLeveringsplikt erLik true}

      	val tilvekstSkogOver200m3 = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.forForekomsterAv(formuesgrunnlagSkogeiendomINorge)
            .summer { fastsatt(sumTilvekst.beloepSomTall) }
        }

        val beløpKapitalinntektSkog = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.forForekomsterAv(kapitalkostnadOgKapitalinntektFraSkogeiendomUtenforNaeringsvirksomhet)
            .summer { kapitalinntekt.beloepSomTall }
        }

       /* val harIkkeKapitalinntektSkog = skattemelding
      		.forForekomsterAv(kapitalkostnadOgKapitalinntektFraSkogeiendomUtenforNaeringsvirksomhet)
      		.harIngenForekomsterDer{kapitalinntekt.beloepSomTall er oppgitt} */

        naeringsspesifikasjon
            .utslagFor {

              val beløpIKontrollisteTømmer = hentKontrolliste(
                    "kontrolliste - toemmeromsetning",
                    listeAv(partsnr)
                ).forForekomsterAv { verdi }
                    .summer { verdiSomTall1 }

               val harIkkeTømmerkonto = harIngenForekomsterDer { skogbruk.skogOgToemmerkonto er oppgitt}

               val harIkkeSalgsinntekt = harIngenForekomsterDer{resultatregnskap.driftsinntekt.salgsinntekt er oppgitt}

               val differanseKapitalinntektOgKontrollistebeløp = beløpKapitalinntektSkog - beløpIKontrollisteTømmer

                hvis((((beløpIKontrollisteTømmer erStørreEnn 0) og (tilvekstSkogOver200m3 erStørreEnn 200)) eller ((beløpIKontrollisteTømmer erStørreEnn 0) og erLeveringspliktig)) og 
                     harIkkeTømmerkonto og harIkkeSalgsinntekt og (differanseKapitalinntektOgKontrollistebeløp erStørreEnn 100000)
                    )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadTips
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning)
    .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }     
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP) og naeringsspesifikasjon.harInnhold})
      {
        // kontrollen gir utslag dersom sats for inntektsfradrag i gevinst- og tapskonto er høyere enn 20 % og grunnlaget er mindre enn kr - 15 000
        // skal IKKE kjøre og gi utslag på rederi (for UP) 
        // kontrollen avviser innsending
        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.gevinstOgTapskonto }
            .utslagFor {
                hvis(hvorMinstEnForekomst {
                    (grunnlagForAaretsInntektsOgFradragsfoering.beloep erMindreEnn -15000) og (satsForInntektsfoeringOgInntektsfradrag erStørreEnn 20)
                }) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil, 1
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning)
    .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }     
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP) og naeringsspesifikasjon.harInnhold
            })
      {
        // kontrollen gir utslag dersom sats for inntektsføring av gevinst- og tapskonto er lavere enn 100 % når grunnlaget er positivt og under kr 15 000
        // skal IKKE kjøre og gi utslag på rederi (for UP) 
        // kontrollen avviser innsending
        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.gevinstOgTapskonto }
            .utslagFor {
                hvis(hvorMinstEnForekomst {
                    ((grunnlagForAaretsInntektsOgFradragsfoering.beloep erStørreEnnEllerLik 15000) og (satsForInntektsfoeringOgInntektsfradrag er oppgitt) og (satsForInntektsfoeringOgInntektsfradrag erMindreEnn 20)) eller
                        (
                            (grunnlagForAaretsInntektsOgFradragsfoering.beloep erMindreEnn 15000) og (grunnlagForAaretsInntektsOgFradragsfoering.beloep erStørreEnn 0) og
                                (satsForInntektsfoeringOgInntektsfradrag erMindreEnn 100)
                            )
                }) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når beløpet fylt ut på feltet 'Kompensasjon for tapt inntekt' avviker fra innrapportert beløp fra NAV. Gjelder både når beløpet er større eller mindre enn innrapportert beløp.
        // kontrollen gir merknad i valideringstjenesten

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(oppgavegiver, sykepengerOgAnnenInntektMvFraOppgavegiver))
            .utslagFor {

                val harKompensasjonNAV = filtrer { grunnlag(behandlingsart) erLik "KOMP_PERS_INNT" }
                    .hvorMinstEnForekomst { grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt }

                val fastsattKompensasjonNAV = filtrer { fastsatt(behandlingsart) erLik "KOMP_PERS_INNT" }
                    .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

                val grunnlagKompensasjonNAV = filtrer { grunnlag(behandlingsart) erLik "KOMP_PERS_INNT" }
                    .summer { grunnlag(beloepSomGruppe.beloepSomTall) }

                val differanseKompensasjonNAV = fastsattKompensasjonNAV - grunnlagKompensasjonNAV

                hvis(harKompensasjonNAV og (differanseKompensasjonNAV erUlik 0)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                        Avviksbeloep settesTil differanseKompensasjonNAV
                    )
                }
            }
        }
    }

sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {

        val harSkattepliktForKreftverk =  skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        val harOpplysningspliktForKraftverk =  opplysningspliktSdfGjeldende
           .forForekomsterAv{opplysningsplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}  

        val harOppgittKraftverk = naeringsspesifikasjon
          .hvorMinstEnForekomst{kraftverk.spesifikasjonAvKraftverk er oppgitt}

        // Kontroll som gir utslag på alle UP/SDF som er kraftverk i skatteplikt/opplysningsplikt
        // som oppgir ugyldig eierandel på kraftverk i næringsspesifikasjonen
        naeringsspesifikasjon
      		.forForekomsterAv{kraftverk.spesifikasjonAvKraftverk}
            .utslagFor {
    			val harKraftverkMedGyldigEierandel = hvorMinstEnForekomst{(eierandel erStørreEnnEllerLik 0) og
                                                                             (eierandel erMindreEnnEllerLik 100)}

                hvis((harSkattepliktForKreftverk eller harOpplysningspliktForKraftverk) og
                     harOppgittKraftverk og
                     ikke(harKraftverkMedGyldigEierandel))
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard   )
                }
            }
    }

