sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {

        val harSkattepliktForKraftverk =  skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}

        val harOpplysningspliktForKraftverk =  opplysningspliktSdfGjeldende
           .forForekomsterAv{opplysningsplikt}
           .hvorMinstEnForekomst{erOmfattetAvSaerreglerForKraftforetak erLik true}  

        val UPSumGrunnlagForNaturressursskatt = skattemeldingUpersonlig
          .summer{kraftverk.samletGrunnlagForNaturressursskatt}

        val UPSumNaturressursskattSpesifisertPrKraftverk = skattemeldingUpersonlig
          .forForekomsterAv{kraftverk.spesifikasjonPerKraftverk}
          .summer{grunnlagForNaturressursskatt}  

        val SDFSumGrunnlagForNaturressursskatt = selskapsmeldingSdf
          .summer{kraftverk.samletGrunnlagForNaturressursskatt}   

        val SDFSumNaturressursskattSpesifisertPrKraftverk = selskapsmeldingSdf
          .forForekomsterAv{kraftverk.spesifikasjonPerKraftverk}
          .summer{grunnlagForNaturressursskatt}    

        // Kontroll som gir utslag på alle UP/SDF som er kraftverk i skatteplikt/opplysningsplikt
        // og det er avvik mellom Samlet grunnlag Naturressursskatt og summen av Naturressursskatt fordelt på kommuner.
        naeringsspesifikasjon
            .utslagFor {

                hvis((harSkattepliktForKraftverk og (UPSumGrunnlagForNaturressursskatt erUlik UPSumNaturressursskattSpesifisertPrKraftverk)) eller 
                     (harOpplysningspliktForKraftverk og (SDFSumGrunnlagForNaturressursskatt erUlik SDFSumNaturressursskattSpesifisertPrKraftverk)))
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard   )
                }
            }
    }

sakstype(Personlig)
        .kontekst(ValideringSkattepliktig)
        .hvis({ naeringsspesifikasjon.harInnhold }) {

            //kontrollen gir utslag dersom skattepliktig skattepliktig krever kreditfradrag for betalt skatt av næringsinntekt og hele/deler av inntekten ikke er tatt med som skattepliktig inntekt
            //kontrollen gir validering i SME

           val skattemessigResultatNSP = naeringsspesifikasjon
          		.forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
                .summer{fordeltSkattemessigResultatEtterKorreksjon.beloep}

    skattemelding
    	.forForekomsterAv{grunnlagForKreditfradragFraNaeringsvirksomhetIUtlandet}    
                .utslagFor {

                  val sumNaeringsinntekt = summer{naeringsinntekt.beloep}

                  val differanseNaeringsinntektSkattemessigResultatNSP = sumNaeringsinntekt - skattemessigResultatNSP

                    hvis(differanseNaeringsinntektSkattemessigResultatNSP erStørreEnn 0)
                    {
                        betjeningsstrategiSettesTil(
                            ikke, merknadStandard, Avviksbeloep settesTil differanseNaeringsinntektSkattemessigResultatNSP
                        )
                    }
                }
        }

sakstype(Upersonlig)
        .kontekst(ValideringSkattepliktig) {

        val selskapIStorbedrift = ansvarligEnhet
            .hvorMinstEnForekomst{erStorbedrift erLik true}

        val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
           .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}   

            // kontrollen gir utslag dersom skattyter krever fradrag for betalt skatt i utlandet uten å legge ved dokumentasjon
            // skal ikke gi utslag dersom skattyter er storbedrift eller skattlegges etter særskilt beskatningsordning for rederiskatteordningen
            // kontrollen gir merknad i valideringstjenesten og SME

    skattemeldingUpersonlig
       	.forForekomsterAv {
            grupper(
                fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilAnnenUtenlandsinntekt.inntektsskattBetaltIUtlandet,
                fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilInntektFraNOKUSSelskapEllerVirksomhetILavskatteland.inntektsskattBetaltIUtlandet,
                fradragINorskFormuesskattForSkattBetaltTilFremmedStatKnyttetTilAnnenUtenlandsinntekt.formuesskattBetaltIUtlandet,
                fradragINorskFormuesskattForSkattBetaltTilFremmedStatKnyttetTilInntektFraNOKUSSelskapEllerVirksomhetILavskatteland.formuesskattBetaltIUtlandet,
            )
        }
        .utslagFor {

            val harLandkodeOgIkkeVedlegg = hvorMinstEnForekomst { (fastsatt(landkode) er oppgitt) og ikke(beloep.beloep har vedleggPåFelt) }

            hvis(ikke(selskapIStorbedrift) og
                 ikke(rederibeskatningsordningBehandlesEtterSaerskilteRegler) og
                 harLandkodeOgIkkeVedlegg)
          {
                betjeningsstrategiSettesTil(
                    ikke, merknadInformasjon
                )
            }
        }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

    val selskapIStorbedrift = ansvarligEnhet
        .hvorMinstEnForekomst{erStorbedrift erLik true}

    val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
        .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
        .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

            // kontrollen gir utslag dersom skattyter mangler dokumentasjon i feltet  "Årets fradragsberettigede andel av underliggende selskapsskatt"
            // skal ikke gi utslag dersom skattyter er storbedrift eller skattlegges etter særskilt beskatningsordning for rederiskatteordningen
            // kontrollen gir merknad i valideringstjenesten og SME    
    skattemeldingUpersonlig
        .utslagFor {
          val harOpplysningerOmKreditIht1631 = hvorMinstEnForekomst{fradragINorskSkattVedSkattleggingAvUtbytteMvFraUtenlandskDatterselskap er oppgitt}

                    hvis(harOpplysningerOmKreditIht1631 og
                        ikke(selskapIStorbedrift) og
                        ikke(rederibeskatningsordningBehandlesEtterSaerskilteRegler)) {
                        betjeningsstrategiSettesTil(
                            ikke, merknadInformasjon
                        )
                    }
                }
        }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

    val selskapIStorbedrift = ansvarligEnhet
        .hvorMinstEnForekomst{erStorbedrift erLik true}

    val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
        .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
        .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

    // kontrollen gir utslag dersom skattyter mangler dokumentasjon i feltet  "Årets fradragsberettigede andel av underliggende selskapsskatt"
    // skal ikke gi utslag dersom skattyter er storbedrift eller skattlegges etter særskilt beskatningsordning for rederiskatteordningen
    // kontrollen gir merknad i valideringstjenesten og SME    
    skattemeldingUpersonlig
       	.forForekomsterAv { fradragINorskSkattVedSkattleggingAvUtbytteMvFraUtenlandskDatterselskap }
        .utslagFor {
          val harVedlegg = hvorMinstEnForekomst { anvendtFradragsberettigetAndelAvUnderliggendeSelskapsskattIInntektsaaret har vedleggPåFelt }

          val andelAvUnderliggendeSelskapsskattErOppgitt = hvorMinstEnForekomst{fastsatt(anvendtFradragsberettigetAndelAvUnderliggendeSelskapsskattIInntektsaaret) er oppgitt}

                    hvis( andelAvUnderliggendeSelskapsskattErOppgitt og 
