sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
     {
        // kontrollen gir utslag dersom skattyters er næringsdrivende (harLeveringsplikt = true i OOSS)
        // og ikke har oppgitt næringsspesifikasjon 
        //       eller opplysninger om deltakers andel i SDF 
        //       eller næringsinntekt fordelt fra ektefelle
        // kontrollen gir merknadMangel i SME

        val erLeveringspliktig = opplysningerOmSkattesubjektGjeldendeDokument
            .forForekomsterAv{opplysningerOmSkattesubjekt}
            .hvorMinstEnForekomst{leveringsplikt.harLeveringsplikt erLik true}

        val harNaeringsspesifikasjon = naeringsspesifikasjon.harInnhold

        val harOppgittNaeringFordeltFraEktefelle = skattemelding
            .forForekomsterAv{naeringsinntektMvFordeltFraEktefelle}
            .hvorMinstEnForekomst { fastsatt(naeringstype) er oppgitt }

        val harOppgittDeltakerinformasjonSDF = skattemelding
            .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
            .hvorMinstEnForekomst { fastsatt(selskapetsOrganisasjonsnummer) er oppgitt }

        skattemelding
            .utslagFor {
            hvis(
                erLeveringspliktig og
                    ikke(harNaeringsspesifikasjon) og
                    ikke(harOppgittNaeringFordeltFraEktefelle) og
                    ikke(harOppgittDeltakerinformasjonSDF)
            ) {
                betjeningsstrategiSettesTil(ikke, faktiskFeil)
            }
        }
        }

