                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }     
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP) 
            })
      {
        // kontrollen gir utslag når grunnlag for inntektsføring er lavere enn - 15 000 og avskrivingssats er mindre enn minimumssatsen for saldogruppen
        // kontrollen gir utslag når grunnlag for inntektsføring er høyere eller lik - 15 000 og avskrivningssats er mindre enn 100 %
        // skal IKKE kjøre og gi utslag på rederi (for UP) 
        // kontrollen medfører merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .utslagFor {
                hvis(hvorMinstEnForekomst {
                    (
                        (saldogruppe erLik "a") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn -15000) og (avskrivningssats erMindreEnn 30)) eller
                            (
                                (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -15000) og
                                    (avskrivningssats erMindreEnn 100)
                                )
                        ) eller
                        (
                            (saldogruppe erLik "c") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn -15000) og (avskrivningssats erMindreEnn 24)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -15000) og
                                        (avskrivningssats erMindreEnn 100)
                                    )
                            ) eller
                        (
                            (saldogruppe erLik "c2") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn -15000) og (avskrivningssats erMindreEnn 30)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -15000) og
                                        (avskrivningssats erMindreEnn 100)
                                    )
                            ) eller
                        (
                            (saldogruppe erLik "d") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn -15000) og (avskrivningssats erMindreEnn 20)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -15000) og
                                        (avskrivningssats erMindreEnn 100)
                                    )
                            ) eller
                        (
                            (saldogruppe erLik "j") og ((grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn -15000) og (avskrivningssats erMindreEnn 10)) eller
                                (
                                    (grunnlagForAvskrivningOgInntektsfoering.beloep erMindreEnn 0) og (grunnlagForAvskrivningOgInntektsfoering.beloep erStørreEnnEllerLik -15000) og
                                        (avskrivningssats erMindreEnn 100)
