    .hvis({ naeringsspesifikasjon.harInnhold }) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val harUtgaaendeVerdiForAnleggsmiddelLavereEnnBeloepsgrenseIFjor = fjoraaretsNaeringsspesifikasjon
          .forForekomsterAv(sti(inntektsaar2022.spesifikasjonAvAnleggsmiddel, inntektsaar2022.saldoavskrevetAnleggsmiddel))
            .filtrer{inntektsaar2022.saldogruppe.finnesBlant("a","c","c2","d","j")}
            .hvorMinstEnForekomst{inntektsaar2022.utgaaendeVerdi.beloepSomTall erMindreEnn -15_000}

        val harIkkeOppgittAvskrivningssats = naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .filtrer{saldogruppe.finnesBlant("a","c","c2","d","j")}
            .harIngenForekomsterDer { fastsatt(avskrivningssats) er oppgitt }

        //Gir utslag hvis utgående verdi for hver forekomst av saldoavskrevetAnleggsmiddel i saldogruppe a, c, c2, d og/eller j i SMIA for forrige inntektsår er lavere enn -15 000
        //og det ikke finnes noen forekomster av avskrivningssats i saldogruppe a, c, c2, d og/eller j.
        //AB-test på skattytere som har tallene 0, 2, 4, 6 eller 8 i 7.siffer i partsnummeret
        //kontrollen gir validering i SME
        naeringsspesifikasjon
            .utslagFor {

                hvis(harUtgaaendeVerdiForAnleggsmiddelLavereEnnBeloepsgrenseIFjor og 
                     harIkkeOppgittAvskrivningssats og 
                     (partsnr.tegnFraIndeks(7).finnesBlant("0","2","4","6","8"))
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadTips
                    )
                }
            }
    }

