            .summer { resultatregnskap.driftskostnad.sumDriftskostnad.beloep }

       skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{naeringsinntektMv}
            .utslagFor {

                val finnesIKontrollisteMva = finnesIKontrolliste(

