sakstype(Personlig)
        .kontekst(ValideringSkattepliktig) {

    // Merknad til skattepliktige med påkostninger på skip og fartøy - informasjon om å opprette eget kort ved påkostninger over 20 % av kostprisen.
    // kontrollen medfører merknad i SME    
    val harVerdierISaldogruppeE = naeringsspesifikasjon
     	.forForekomsterAv{spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel}
        .filtrer{fastsatt(saldogruppe) erLik "e"}
        .hvorMinstEnForekomst{(fastsatt(inngaaendeVerdi.beloep) er oppgitt) og
        						(fastsatt(paakostning.beloep) er oppgitt) og
        						(fastsatt(erDetFysiskAnleggsmiddelIUtgaaendeVerdi) erLik "true")}

    val forekomsterAvSkip = skattemelding
       	.forForekomsterAv{formuesspesifikasjonForSkipFiskeOgFangstfartoeyMv}
        .antall

    skattemelding
        .fokusområde("Fokusområde")
        .forForekomsterAv{formuesspesifikasjonForSkipFiskeOgFangstfartoeyMv}
        .utslagFor {
          val harKostprisEllerPåkostningOver20Prosent = hvorMinstEnForekomst {kostprisEllerAnskaffelsespris.beloep er oppgitt}

          	hvis(harVerdierISaldogruppeE og harKostprisEllerPåkostningOver20Prosent og (forekomsterAvSkip erLik 1))
          	{
            	betjeningsstrategiSettesTil(
                 	ikke, merknadInformasjon
                        )
                    }
                }
        }

sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig)
      {
        val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding = skattemeldingUpersonlig
             .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
             .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
            .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
            .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

       val deltakersAndelAvFinansinntektOgFinansunderskuddISdf = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .summer{andelAvSkattemessigFinansoverskuddFraSdfOgNokus}    

       val deltakersAndelAvFinansinntektOgFinansunderskuddIUp = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .summer{andelAvSkattemessigFinansoverskuddFraSdfOgNokus}

       val samletFormueOgInntektSdf = selskapsmeldingSdf
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
           .summer{andelAvSamletInntektRederiskatteordningen}

      val samletFormueOgInntektUp = skattemeldingUpersonlig
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
           .summer{andelAvSamletInntektRederiskatteordningen}

       val sumFinansinntektOgFormueUlikSamletFormueOgInntektISdf = 
             deltakersAndelAvFinansinntektOgFinansunderskuddISdf erUlik samletFormueOgInntektSdf

       val sumFinansinntektOgFormueUlikSamletFormueOgInntektIUp = 
             deltakersAndelAvFinansinntektOgFinansunderskuddIUp erUlik samletFormueOgInntektUp

       val ikkeSamletInntektIDeltakerkort = selskapsmeldingSdf
           .forForekomsterAv{deltaker}
           .hvorMinstEnForekomst{deltakersAndelAvInntektOmfattetAvRederiskatteordningen.andelAvFinansinntekt ikkeEr oppgitt}

        // Gir utslag hvis innsending gjelder Upersonlig og SDF
        // Selskapet/deltaker er innenfor rederiskatteordningen og
        // Rederiskatteordning.FinansinntektOgFinansunderskudd.andelAvSkattemessigFinansoverskuddFraSdfOgNokus er ulik  
        //summen av FormueogInntektSelskapMedDeltakerfastsetting.Rederiskatteordning.andelAvSamletInntekt 
        // Skal også slå ut dersom samlet inntekt i deltakerkort ikke er fylt ut i det hele tatt
      naeringsspesifikasjon
        .utslagFor {

        hvis((harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding og
              sumFinansinntektOgFormueUlikSamletFormueOgInntektIUp) eller
             (harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og
             sumFinansinntektOgFormueUlikSamletFormueOgInntektISdf og 
              ikkeSamletInntektIDeltakerkort)
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig)
      {
        val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding = skattemeldingUpersonlig
             .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
             .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
            .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
            .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

       val deltakersAndelAvTonnasjeskattSdf = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .summer{andelTonnasjeskattFraSdf}    

       val deltakersAndelAvTonnasjeskattUp = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .summer{andelTonnasjeskattFraSdf}

       val samletTonnasjeskattSdf = selskapsmeldingSdf
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
           .summer{andelAvTonnasjeskatt}

       val samletTonnasjeskattUp = skattemeldingUpersonlig
          .forForekomsterAv{deltakersAndelAvFormueOgInntekt}
           .summer{andelAvTonnasjeskatt}

      val sumTonnasjeskattUlikISdf = deltakersAndelAvTonnasjeskattSdf erUlik samletTonnasjeskattSdf

      val sumTonnasjeskattUlikIUp = deltakersAndelAvTonnasjeskattUp erUlik samletTonnasjeskattUp

      val ikkeTonnasjeskattIDeltakerkort = selskapsmeldingSdf
           .forForekomsterAv{deltaker}
           .hvorMinstEnForekomst{deltakersAndelAvInntektOmfattetAvRederiskatteordningen.andelAvTonnasjeskatt ikkeEr oppgitt}

        // Gir utslag hvis innsending gjelder Upersonlig og SDF
        // Selskapet/deltaker er innenfor rederiskatteordningen og
        // Andel tonnasjeskatt fra sdf og NOKUS under Rederibeskatningsordninger er ulik
        //summen av andel av tonnasjeskatt under Formue og inntekt i SDF
        // Skal også slå ut dersom tonnasjeskatt i deltakerkort ikke er fylt ut i det hele tatt
      naeringsspesifikasjon
        .utslagFor {

        hvis((harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding og
              sumTonnasjeskattUlikIUp) eller
              (harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og 
               sumTonnasjeskattUlikISdf og ikkeTonnasjeskattIDeltakerkort)
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig)
      {
       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding = skattemeldingUpersonlig
             .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
             .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

       val harInntektstillegg = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.inntektstilleggForHoeyEgenkapital}
           .hvorMinstEnForekomst{inntektstillegg erStørreEnn 0} 

       val sumGjeldVedUtgangenAvAaret = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.inntektstilleggForHoeyEgenkapital}
           .summer{egenGjeldVedUtgangenAvInntektsaaret}

        // Gir utslag hvis innsending gjelder Upersonlig 
        // Selskapet er innenfor rederiskatteordningen og
        // Rederiskatteordning.InntektstilleggForHøyEgenkaptial.inntektstillegg >0 og
        // Rederiskatteordning.InntektstilleggForHøyEgenkapital.egenGjeldVedUtgangenAvInntektsåret er ulik
        //Næringsspesifikasjon: Summen av langsiktig og kortsiktig gjeld
      naeringsspesifikasjon
        .utslagFor {

        val sumAvKortsiktigGjeld =
           forForekomsterAv{balanseregnskap.gjeldOgEgenkapital.kortsiktigGjeld}
           .forForekomsterAv{gjeld}
           .summer{beloep.beloep}

        val sumAvLangsiktigGjeld =
           forForekomsterAv{balanseregnskap.gjeldOgEgenkapital.langsiktigGjeld}
           .forForekomsterAv{gjeld}
           .summer{beloep.beloep}

        val sumKortsiktigOgLangsiktigGjeld = sumAvKortsiktigGjeld + sumAvLangsiktigGjeld

        hvis(
             harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding og
              harInntektstillegg og
             (sumGjeldVedUtgangenAvAaret erUlik sumKortsiktigOgLangsiktigGjeld)
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

