           .summer{selskapsandelIProsent}

         val sumDeltakerssAndelIProsent = forForekomsterAv{deltaker}
           .summer{deltakersAndelAvInntektIProsent}

      hvis(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og ikke(harDeltakerErNokus) og
            ((sumDeltakersAndelAvUnderskudd erUlik (sumSamletFinansunderskudd * sumSelskapetsAndelIProsent/100)) eller
             (sumDeltakersAndelAvUnderskudd erUlik (sumSamletFinansunderskudd * sumDeltakerssAndelIProsent/100)))
