sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig){

      val harIkkeErOmfattetAvPetroleumsskattelovenUp = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik false}

       val harIkkeErOmfattetAvPetroleumsskattelovenSdf = opplysningspliktSdfGjeldende
          .forForekomsterAv{opplysningsplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik false}

     val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding = skattemeldingUpersonlig
          .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
          .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

      val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
           .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
           .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

      val harBegrensningRentefradragIKonsernOgNaerstaaendeUp = skattemeldingUpersonlig
          .hvorMinstEnForekomst{rentebegrensning er oppgitt}

      val harBegrensningRentefradragIKonsernOgNaerstaaendeSdf = selskapsmeldingSdf
         .hvorMinstEnForekomst{rentebegrensning er oppgitt}

        // Gir utslag hvis Innsending gjelder upersonlig skattepliktig eller SDF
        // Tema Begrensning av rentefradrag i konsern og mellom nærstående er utfylt og
        // SUM [ Næringsspesifikasjon/Finanskostnader/Kodeliste="8130 Rentekostnad til foretak i samme konsern" OG 
        //Næringsspesifikasjon/Finanskostnader/Kodeliste="8150 Annen rentekostnad" fratrukket 
        //PermanentForskjell/permanentForskjellstype/kodeliste="Tillegg for rentekostnader det ikke kreves fradrag for" ] > null
        // Skal ikke gi utslag på selskaper som omfattes av særbeskatning for petroleum og rederi
      naeringsspesifikasjon
        .utslagFor {
         val sumRemtekostnadNS = 
            forForekomsterAv { resultatregnskap.finanskostnad }
            .forForekomsterAv { kostnad }
            .filtrer {fastsatt(type) erLik "8130"} 
            .summer{beloep.beloep}  

         val sumAnnenRentekostnadNS = 
            forForekomsterAv { resultatregnskap.finanskostnad }
            .forForekomsterAv { kostnad }
            .filtrer {fastsatt(type) erLik "8150"} 
            .summer{beloep.beloep}  

         val sumTypePermanentForskjell = 
             forForekomsterAv { permanentForskjell }
             .filtrer {fastsatt(permanentForskjellstype) erLik "tilleggForRentekostnad"}
             .summer{beloep.beloep}       

        val sumKostnad = sumRemtekostnadNS + sumAnnenRentekostnadNS + sumTypePermanentForskjell

          hvis((harIkkeErOmfattetAvPetroleumsskattelovenUp og 
                ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding) og
                harBegrensningRentefradragIKonsernOgNaerstaaendeUp og (sumKostnad erStørreEnn 0)) eller
                 (harIkkeErOmfattetAvPetroleumsskattelovenSdf og
                 ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen) og
                 harBegrensningRentefradragIKonsernOgNaerstaaendeUp og (sumKostnad erStørreEnn 0)))
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

