sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
     .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP)
            })
      {
        // kontrollen gir utslag når det rapporteres nyanskaffelse på en eksisterende enkeltesaldo
        // kontrollen medfører merknad i valideringstjenesten
        // skal IKKE kjøre og gi utslag på rederi (for UP)
        naeringsspesifikasjon
            .forForekomsterAv{spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel}
            .filtrer { fastsatt(saldogruppe).finnesBlant("e", "f", "g", "h", "i") }
            .utslagFor {
                val harOppgittInngaaendeVerdiOgNyanskaffelse = hvorMinstEnForekomst{(inngaaendeVerdi.beloep er oppgitt) og (nyanskaffelse.beloep er oppgitt)}

                hvis(harOppgittInngaaendeVerdiOgNyanskaffelse) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) 
     .hvis({
          val RederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
              .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
              .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 erRederibeskatningsordningBehandlesEtterSaerskilteRegler
                }     
                 ikke(RederibeskatningsordningBehandlesEtterSaerskilteReglerUP) 
            })
      {
        // kontrollen gir utslag dersom det oppgis opplysninger om forretningseiendom anskaffet før 01.01.84 og ervervsdatoen er senere enn 01.01.84
        // skal IKKE kjøre og gi utslag på rederi (for UP) 
        // kontrollen medfører merknad i valideringstjenesten
        val forretningsbygg = naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .hvorMinstEnForekomst {
                (
                    (forretningsbyggAnskaffetFoer01011984.historiskKostpris.beloep er oppgitt)
                        eller (forretningsbyggAnskaffetFoer01011984.nedskrevetVerdiPr01011984.beloep er oppgitt)
                        eller (forretningsbyggAnskaffetFoer01011984.nedreGrenseForAvskrivning.beloep er oppgitt)
                    )
            }

        val datoGrense = "1984-01-01".dato
        naeringsspesifikasjon
            .forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
            .utslagFor {
                hvis(hvorMinstEnForekomst { (forretningsbygg) og (ervervsdato er oppgitt) og (ervervsdato.dato erEtter datoGrense) }) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig)
     .hvis({
          val storbedriftEllerRederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

          val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
