sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag når det er rapportert driftskostnader på skog- og tømmerkonto, men driftskostnadene er ikke rapportert i resultatregnskapet. 
        // kontrollen gir merknad i valideringstjenesten

        naeringsspesifikasjon
                .utslagFor {
                val sumInntektsfradragFraTømmerkonto = forForekomsterAv { skogbruk.skogOgToemmerkonto }
                    .summer { inntektOgKostnad.driftskostnader.beloep }

                val harSkog = forForekomsterAv { skogbruk.skogOgToemmerkonto }
                    .hvorMinstEnForekomst{inntektOgKostnad.driftskostnader.beloep er oppgitt}

              	val beløpOverførtTilTømmerkonto = forForekomsterAv { resultatregnskap.driftskostnad.annenDriftskostnad }
                    .forForekomsterAv { kostnad }
                    .filtrer { type erLik "7910" }
                    .summer { beloep.beloep }

                    val sumDriftskostnaderIRegnskapet = summer { resultatregnskap.driftskostnad.sumDriftskostnad.beloep }

                  hvis(harSkog og (sumInntektsfradragFraTømmerkonto erStørreEnn (sumDriftskostnaderIRegnskapet - beløpOverførtTilTømmerkonto))
                          ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1 )
                }
            }
    }

sakstype(Personlig)
            .kontekst(ValideringSkattepliktig) {

        // Merknad til skattepliktige når salgsinntekter i skog og tømmer er høyere enn salgsinntekter i resultatregnskapet
        // kontrollen medfører merknad i SME

         val harSalgsinntekUnderSkogbruk = naeringsspesifikasjon
         	.forForekomsterAv { skogbruk.skogOgToemmerkonto }
            .summer{(fastsatt(inntektOgKostnad.salgsinntektOgUttakFraSkurtoemmerMassevirkeRotsalgMv.beloep)) +
            		(fastsatt(inntektOgKostnad.salgsinntektOgUttakAvVedOgBiomasse.beloep))  +
            		(fastsatt(inntektOgKostnad.andreDriftsinntekter.beloep))}

        val harIkkeSalgsinntekt = naeringsspesifikasjon
            .harIngenForekomsterDer{resultatregnskap.driftsinntekt.salgsinntekt er oppgitt}

    naeringsspesifikasjon
      	.utslagFor {
          	  val harIkkeSalgsinntekt = forForekomsterAv { resultatregnskap.driftsinntekt.salgsinntekt }
                  .harIngenForekomsterDer{inntekt er oppgitt}

              val harIkkeAnnenDriftsinnekt = forForekomsterAv { resultatregnskap.driftsinntekt.annenDriftsinntekt }
    			 .forForekomsterAv { inntekt }
                 .harIngenForekomsterDer {fastsatt(type) erLik "3900" }

              val salgsinntekterIResultat = forForekomsterAv { resultatregnskap.driftsinntekt.salgsinntekt }
    			 .forForekomsterAv { inntekt }
                 .filtrer {fastsatt(type).finnesBlant("3000","3100","3200") }
                 .summer {beloep.beloep }

              val andreDriftsinntekterIResultat = forForekomsterAv { resultatregnskap.driftsinntekt.annenDriftsinntekt }
    			 .forForekomsterAv { inntekt }
                 .filtrer {fastsatt(type) erLik "3900" }
                 .summer {beloep.beloep }

              val sumInntekterIResultat = salgsinntekterIResultat + andreDriftsinntekterIResultat

          	  val avvikSkogbrukOgResultat = harSalgsinntekUnderSkogbruk - sumInntekterIResultat

              	hvis(((harSalgsinntekUnderSkogbruk erStørreEnn 0) og (avvikSkogbrukOgResultat erStørreEnn 1000)) eller
                     (harIkkeSalgsinntekt og harIkkeAnnenDriftsinnekt og (harSalgsinntekUnderSkogbruk erStørreEnn 0))
              	){
                	betjeningsstrategiSettesTil(
                     	ikke, merknadTips, Avviksbeloep settesTil avvikSkogbrukOgResultat,
                      kortTypeReferanse settesTil "salgsinntekt"
                            )
                        }
                    }
            }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) 
    {

       val harVirksomhetstypeBankFinansieringsforetak = naeringsspesifikasjon
      		.forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst{virksomhetstype erLik "bankOgFinansieringsforetak"}

       val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
           .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"} 

       val organisasjonErNUF = partInfo
            .forForekomsterAv { organisasjonsform }
            .hvorMinstEnForekomst { organisasjonsform erLik "norskregistrertUtenlandskForetak" }   

       val sumLangsiktigFordringMellomvaerendeMotparter = skattemeldingUpersonlig
           .forForekomsterAv{transaksjonsmotpart}
           .forForekomsterAv{mellomvaerende}
           .filtrer{fastsatt(mellomvaerendetype) erLik "langsiktigFordring"}
           .summer{beloep}

       val sumLaanAnleggsmiddel = naeringsspesifikasjon
            .forForekomsterAv { balanseregnskap.anleggsmiddel.balanseverdiForAnleggsmiddel }
            .forForekomsterAv { balanseverdi }
            .filtrer {fastsatt(type) erLik "1320"}
            .summer{beloep.beloep}

       val differanseLangsiktigFordringSkattemeldingOgNS = differanse(sumLangsiktigFordringMellomvaerendeMotparter, sumLaanAnleggsmiddel)     

       val harAvkreftetUnntakFraPliktTilAaLevere = skattemeldingUpersonlig
           .forForekomsterAv{unntakForOppgaveOgDokumentasjonsplikt}
           .hvorMinstEnForekomst{(harUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende er oppgitt) og
                                 (harUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende erLik false)}

        // Kontrollen gir utslag hvis 
        // 'Langsiktige fordringer' i skattemeldingen <> '1320 Lån til foretak i samme konsern'i NS
        // Og foretaket i kort "Kontrollerte transaksjoner/disposisjoner og mellomværender" har valgt "Nei" i 
        //    feltet 'Har unntak fra plikten til å levere temakortet "Kontrollerte transaksjoner/disposisjoner og mellomværender" 
        // Skal bare gi utslag når avviket er på minst 1 000 000
        //Skal IKKE gjelde for bank og forsikring, rederi og utenlandske selskap med begrenset skatteplikt
        skattemeldingUpersonlig
          .utslagFor {

            val harUtenlanskOppdragstakerKontraktsnummer = forForekomsterAv{opplysningOmSkattesubjekt}
              .forForekomsterAv{beskrivelseAvOppdragForUtenlandskOppdragstaker}
              .hvorMinstEnForekomst{kontraktsnummer er oppgitt}

            hvis(ikke(harVirksomhetstypeBankFinansieringsforetak) og
                  ikke(rederibeskatningsordningBehandlesEtterSaerskilteRegler) og
                  (ikke(organisasjonErNUF) og ikke(harUtenlanskOppdragstakerKontraktsnummer)) og 
                  (differanseLangsiktigFordringSkattemeldingOgNS erStørreEnnEllerLik 1_000_000) og
                  harAvkreftetUnntakFraPliktTilAaLevere
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadStandard
                )
            }
        }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) 
    {
       val sumLangsiktigGjeldMellomvaerendeMotparter = skattemeldingUpersonlig
