sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) 
    {           

        // Kontrollen gir utslag hvis 
        // egenfastsatt skattefradrag for kostnader til forskning og utvikling > 0, 
        // og ikke angitt "forskningOgEllerUtvikling" som funksjonstype under funksjon og risiko 
        // og har valgt "Nei" for unntak i feltet 'Har unntak fra plikten til å levere temakortet "Kontrollerte transaksjoner/disposisjoner og mellomværender"'
        skattemeldingUpersonlig
          .utslagFor {

            val harOppgittSkattefradragForFOU = forForekomsterAv{spesifikasjonAvSkattefradragForKostnaderTilForskningOgUtvikling}
               .hvorMinstEnForekomst{fastsatt(samletSkattefradrag) erStørreEnn 0} 

            val harIkkeOppgittFunksjonstypeFOU = forForekomsterAv{funksjonOgRisiko}
               .harIngenForekomsterDer{funksjonstype erLik "forskningOgEllerUtvikling"}  

            val harIkkeUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende = forForekomsterAv{unntakForOppgaveOgDokumentasjonsplikt}
               .hvorMinstEnForekomst{fastsatt(harUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende) erLik false}


            hvis(harOppgittSkattefradragForFOU og
                 harIkkeOppgittFunksjonstypeFOU og
                 harIkkeUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadStandard
                )
            }
        }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) 
    {

         val sumOperasjonselleInntekterMotparter = skattemeldingUpersonlig
           .forForekomsterAv{transaksjonsmotpart}
           .summer{ samletOperasjonellInntektPerTransaksjonsmotpart}

         val sumKostnadFellesAdministrativeTjenesterMotparter = skattemeldingUpersonlig
           .forForekomsterAv{transaksjonsmotpart}
           .forForekomsterAv{transaksjonOgDisposisjon}
           .filtrer{fastsatt(transaksjonstype) erLik "mottattKostnadsdekningVedroerendeFellesAdministrativTjenesteMv"}
           .summer{beloep}

         val sumDriftsinntekter = naeringsspesifikasjon
            .summer { resultatregnskap.driftsinntekt.sumDriftsinntekt.beloep }

         val differanseOperasjonelleInntekterMinusAdministrativeKostnaderDriftsinntekter = (sumOperasjonselleInntekterMotparter - sumKostnadFellesAdministrativeTjenesterMotparter) - sumDriftsinntekter

         val harAvkreftetUnntakFraPliktTilAaLevere = skattemeldingUpersonlig
           .forForekomsterAv{unntakForOppgaveOgDokumentasjonsplikt}
           .hvorMinstEnForekomst{(harUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende er oppgitt) og
                                 (harUnntakFraPliktenTilAaLevereKontrollertTransaksjonDisposisjonOgMellomvaerende erLik false)}  

        // Kontrollen gir utslag hvis 
        // 'Samlede operasjonelle inntekter alle motparter' minus "Kostnadsdekning mottatt vedrørende felles administrative tjenester mv." er større enn sum driftsinntekter
        // Og foretaket i kort "Kontrollerte transaksjoner/disposisjoner og mellomværender" har valgt "Nei" i feltet 'Har unntak fra plikten til å levere temakortet "Kontrollerte transaksjoner/disposisjoner og mellomværender" 
        // Skal bare ha utslag hvis avviket er på minst 100 000.
        skattemeldingUpersonlig
          .utslagFor {
            hvis( (differanseOperasjonelleInntekterMinusAdministrativeKostnaderDriftsinntekter erStørreEnnEllerLik 100_000) og
                  harAvkreftetUnntakFraPliktTilAaLevere
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadStandard
                )
            }
        }
    }

