                    forForekomsterAv { resultatregnskap.driftskostnad.annenDriftskostnad }
                        .forForekomsterAv { kostnad }
                        .filtrer { type.finnesBlant("7000", "7020", "7040") }
                        .summer { beloep.beloep }

                hvis(sumKostnaderTransportmiddel erStørreEnn sumDriftskostnaderBilIResultatregnskapet) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
