sakstype(Personlig)
        .kontekst(ValideringSkattepliktig) {

    // Merknad til skattepliktige når har tømmerkonto men ikke næringstype skogbruk
    // kontrollen medfører merknad i SME

    val harInntektFradragFraTømmerkonto = naeringsspesifikasjon
     	.forForekomsterAv { skogbruk.skogOgToemmerkonto }
        .summer{(fastsatt(inntektFraToemmerkonto.beloep)) +
        		(fastsatt(inntektsfradragFraToemmerkonto.beloep))}

    val harIkkeInntektFradragPåTømmerkonto = naeringsspesifikasjon
       	.forForekomsterAv { skogbruk.skogOgToemmerkonto }
        .summer{(fastsatt(driftsresultatSomKanOverfoeresTilToemmerkonto.beloep)) }

    naeringsspesifikasjon
        .fokusområde("FokusFordeltBeregnetNaeringsinntekt")
        .utslagFor {
          val harIkkeNæringstypeSkogbruk = forForekomsterAv { fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf }
          	.harIngenForekomsterDer {(naeringstype erLik "skogbruk")}

          	hvis(((harInntektFradragFraTømmerkonto erUlik 0) eller (harIkkeInntektFradragPåTømmerkonto erUlik 0)) og harIkkeNæringstypeSkogbruk)
          	{
            	betjeningsstrategiSettesTil(
                 	ikke, merknadStandard
                        )
                    }
                }
        }

// Kontrollen gir utslag når selskap rapporterer ufullstendige opplysninger knyttet til skatte- og avgiftsordrninger. Alle opplysningene må fylles ut.
    // KOntrollen gir merknad i valideringstjenesten.
    sakstype(Upersonlig)
        .kontekst(ValideringSkattepliktig) {

            val gyldigeStøtteordninger = skatteOgAvgiftsordningOmfattetAvOffentligStoette_2023
                .forForekomsterAv { kode }
                .verdierFor { tekniskNavn }

            val harStøtteordning = skattemeldingUpersonlig
                .forForekomsterAv {
                    spesifikasjonAvForholdRelevanteForBeskatning.tilleggsinformasjonKnyttetTilMottakAvSkatteOgAvgiftsordningOmfattetAvOffentligStoette.skatteOgAvgiftsordningOmfattetAvOffentligStoette
                }
                .hvorMinstEnForekomst {
                    (fastsatt(skatteOgAvgiftsordning) er oppgitt) og
                        (fastsatt(skatteOgAvgiftsordning).finnesBlant(gyldigeStøtteordninger))
                }

            skattemeldingUpersonlig
                .forForekomsterAv {
                    spesifikasjonAvForholdRelevanteForBeskatning.tilleggsinformasjonKnyttetTilMottakAvSkatteOgAvgiftsordningOmfattetAvOffentligStoette
                }
                .utslagFor {
                    hvis(hvorMinstEnForekomst {
                        harStøtteordning og (varIOekonomiskeVanskerVedSistAvlagtRegnskapsaar ikkeEr oppgitt) eller
                            (erIKonsern ikkeEr oppgitt) eller                            
                            (harMottattAnnenOffentligStoette ikkeEr oppgitt)
                    }) {
                        betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                    }
                }
        }

sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattepliktig med virksomhetstype aksjeselskap rapporterer ett eller flere av følgende felter
        // fordeltBeregnetNaeringsinntekt.sjablongberegnetInntektFraBiomasseVedproduksjon
        // fordeltBeregnetNaeringsinntekt.fradragForRenterINaeringPaaSvalbard
        // fordeltBeregnetNaeringsinntekt.fremfoertUnderskuddIEnkeltpersonforetakPaaSvalbardFraTidligereAar
        // fordeltBeregnetNaeringsinntekt.korreksjonFraReindrift
        // kontrollen gir faktiskFeil i valideringstjenesten

        val harVirksomhetstypeUlikEnkeltpersonforetak = naeringsspesifikasjon
            .forForekomsterAv{virksomhet}
            .hvorMinstEnForekomst {
                virksomhetstype.finnesBlant("oevrigSelskap", "selskapMedDeltakerfastsetting", "bankOgFinansieringsforetak",
                                            "livsforsikringsforetakOgPensjonskasse", "skadeforsikringsforetak")
            }

        val rapportererUgylidigFelt = naeringsspesifikasjon
            .forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
            .hvorMinstEnForekomst {
                (
                    (sjablongberegnetInntektFraBiomasseVedproduksjon.beloep er oppgitt) eller
                  	(sjablongberegnetFradragFraBiomasseVedproduksjon.beloep er oppgitt) eller
                        (fradragForRenterINaeringPaaSvalbard.beloep er oppgitt) eller
                        (fremfoertUnderskuddIEnkeltpersonforetakPaaSvalbardFraTidligereAar.beloep er oppgitt) eller
                        (korreksjonFraReindrift.beloep er oppgitt)
                    )
            }

        naeringsspesifikasjon
            .utslagFor {

