    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har økt skattepliktig tap på obligasjoner på 500 000 eller mer
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv{obligasjonOgSertifikat}
            .utslagFor {
                val differanseTapStoerreEnnBeloepsgrense = hvorMinstEnForekomst{differanse(fastsatt(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloep), 
                                               grunnlag(skattepliktigTapVedRealisasjonAvObligasjonOgSertifikat.fradragsberettigetBeloep.beloep)) erStørreEnnEllerLik 500_000}

                hvis(ikke(harInnsending) og differanseTapStoerreEnnBeloepsgrense) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

      val skattepliktForekomster = skattepliktGjeldende
      .forForekomsterAv { skatteplikt }

      val erIkkeSaerskiltSkattepliktig = skattepliktForekomster
        .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
        .harIngenForekomsterDer {
          type.finnesBlant(
            "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet",
            "skattepliktEtterPetroleumsskatteloven",
            "skattepliktSomUtenrikstjenestemann",
            "skattepliktSomSjoemann",
            "skattepliktAvNaeringsdriftEiendomMv",
            "kildeskattepliktPaaLoenn",
            "kildeskattepliktPaaPensjon",
            "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato"
          )
        }

      val harGlobalSkattepliktTilNorge = skattepliktForekomster
        .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

      val erSkattepliktigTilSvalbard = skattepliktForekomster
        .hvorMinstEnForekomst {
          personligSkattesubjekt.skattepliktTilSvalbard.finnesBlant(
            "global",
            "begrenset",
            "skattepliktUtenOpphold"
          )
        }

      val harDnr = skattepliktForekomster
        .hvorMinstEnForekomst {
          (personligSkattesubjekt.norskPersonidentifikator.dnummer er oppgitt) og
          personligSkattesubjekt.norskPersonidentifikator.dnummer.tegnFraIndeks(1)
            .finnesBlant("4", "5", "6", "7", "8", "9")
        }

      val erDoedsboNorgeEllerSvalbard = skattepliktForekomster
        .hvorMinstEnForekomst{((doedsbo.skattepliktTilNorge er oppgitt) og (doedsbo.skattepliktTilNorge erUlik "ingenSkatteplikt")) eller
          ((doedsbo.skattepliktTilSvalbard er oppgitt) og (doedsbo.skattepliktTilSvalbard erUlik "ingenSkatteplikt"))}

      val harOppgittOmberegningsresultat = skattemelding
      .harIngenForekomsterDer { omberegningsresultatForEtterbetaltePensjonerOgTrygdeytelser er oppgitt }

      // kontrollen gir utslag dersom skattyter har etterbetaling på 50 000 eller mer
      // Skal gi utslag på de som har gobal skatteplikt til Norge
      // Skal IKKE gi utslag på de med skatteplikt til Svalbard, er dødsbo eller som har særskilt skatteplikt
      // skal heller ikke gi utslag på de med dnr (fnr som starter med høyere tall enn 3)
      skattemelding
        .fokusområde("Fokusområde")
        .utslagFor {
          val sumEtterbetaltLoenn = forForekomsterAv{arbeidsgiver}
              .forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
              .filtrer { grunnlag(erEtterbetaling) erLik "true" }
              .summer { grunnlag(beloep.beloep) }

            val sumEtterbetaltPensjon = forForekomsterAv{pensjonsinntekt}
              .filtrer { grunnlag(erEtterbetaling) erLik "true" }
              .summer { grunnlag(pensjonsinntektPrSkattleggingsperiode.beloep) }

          hvis(harGlobalSkattepliktTilNorge og
               ikke(erDoedsboNorgeEllerSvalbard) og
               ikke(harInnsending) og
               harOppgittOmberegningsresultat og
               ((sumEtterbetaltLoenn erStørreEnnEllerLik 50_000) eller (sumEtterbetaltPensjon erStørreEnnEllerLik 50_000)) og
               erIkkeSaerskiltSkattepliktig og
               ikke(erSkattepliktigTilSvalbard) og
               ikke(harDnr)
              ) {
            betjeningsstrategiSettesTil(
              ikke,
              merknadInformasjon
            )
          }
        }
    }

