             .utslagFor {
                 val sumPensjonTrygdPerUtbetaler = forForekomsterAv{perMaaned}
                     .summer { pensjonsinntekt.beloep }

                 val differansePensjonTrygd =
                     differanse(grunnlag(sumPensjonTrygdPerUtbetaler), fastsatt(sumPensjonTrygdPerUtbetaler))

                 hvis(ikke(harInnsending) og 
                      (differansePensjonTrygd erStørreEnn 100)) {
                     betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                 }
             }
         }

