sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har grunnlag hvor pensjon kommer fra Danmark eller Sverige
        // og de ikke har endret pensjonsinntekten, og det ikke er oppgitt metode for dobbeltbeskatning
        // skal ikke gi utslag hvis pensjon ligger med type "Pensjon fra annen EØS-stat som tilsvarer alderspensjon og
        //  AFP i Norge" 
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { (grunnlag(landkode) erLik "SE") eller (grunnlag(landkode) erLik "DK") }
            .utslagForFørste {
                val harIkkeOppgittDobbeltbeskatningsmetodePaaLoennFraDanmarkEllerSverige =
                    forForekomsterAv(perMaaned)
                        .hvorMinstEnForekomst {
                            (grunnlag(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt) og
