                hvis((partsnr.tegnFraIndeks(7).finnesBlant("1", "2", "3", "4", "9")) og                
                    ikke(harInnsending) og
                    harUtgifterVedRefinansieringAvLaan)
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadBekreft, avhukingsboks)
                 }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // hvis sluttbruker har lagt inn sum reisefradrag
        // og ikke har lagt spesifikasjon av hverken arbeidsreise eller pendler/besøksreise innenfor eller utenfor EØS
       skattemelding
            .forForekomsterAv{reiserIArbeidsforhold}
            .utslagFor {

                val harOppgittReisefradrag = hvorMinstEnForekomst{
                  (fastsatt(reisefradrag.fradragsberettigetBeloep.beloep) erStørreEnn 0) eller 
                  (fastsatt(reisefradrag.beloepUtenHensynTilValgtPrioritertFradragstype.beloep) erStørreEnn 0)
                }

                val harIkkeOppgittspesifikasjonAvArbeidsEllerBesoeksreise = harIngenForekomsterDer{
                  (arbeidsreise er oppgitt) eller
                  (besoeksreiseTilHjemInnenforEOeS er oppgitt) eller
                  (besoeksreiseTilHjemUtenforEOeS er oppgitt)
                }

                hvis(harOppgittReisefradrag og harIkkeOppgittspesifikasjonAvArbeidsEllerBesoeksreise) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen skal gi utslag og merknad dersom skattyter har oppgitt arbeidsreise eller besøæksreise
        // uten å ha oppgitt lønn eller næringsspesifikasjon eller opplysninger om deltakers andel i SDF
        val harOppgittNaeringsspesifikasjon = naeringsspesifikasjon
            .hvorMinstEnForekomst { naeringsspesifikasjon.partsreferanse er oppgitt }

        val harIKkeOppgittNaeringsInfoISkattemeldingen = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.harIngenForekomsterDer { fastsatt(naeringsinntektMv) er oppgitt }
        }

        val harIkkeOppgittDeltakerinformasjonSDF = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.harIngenForekomsterDer { fastsatt(deltakersAndelAvFormueOgInntekt) er oppgitt }
        }

        val harIkkeOppgittLoenn = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.harIngenForekomsterDer { fastsatt(arbeidsgiver) er oppgitt }
        }

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(reiserIArbeidsforhold)
            .forForekomsterAv(
                gruppe(
                    arbeidsreise,
                    besoeksreiseTilHjemInnenforEOeS,
                    besoeksreiseTilHjemUtenforEOeS
                )
            )
            .utslagFor {
                hvis(
                    (antallReiser erStørreEnn 0) og
                        (ikke(harOppgittNaeringsspesifikasjon) og harIKkeOppgittNaeringsInfoISkattemeldingen og harIkkeOppgittDeltakerinformasjonSDF og harIkkeOppgittLoenn)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

