    .kontekst(ValideringFoerBeregning) {

        // hvis sluttbruker har lagt inn info om arbeidsreise mellom hjem og arbeid og ikke har lagt inn adresse for arbeidssted eller hjemsted (obligatoriske felter i SME)
       skattemelding
            .forForekomsterAv{reiserIArbeidsforhold}
            .utslagFor {

                val harOppgittarbeidsreiseMedMangelfullAdresse = forForekomsterAv{arbeidsreise}
                   .hvorMinstEnForekomst {
                      ((fastsatt(antallReiser) er oppgitt) eller
                      (fastsatt(lengdeAvReiseTurRetur) er oppgitt) eller
                      (fastsatt(egenandelVedReiseDekketAvArbeidsgiver.beloep) er oppgitt) eller
                      (fastsatt(sparerMerEnnToTimerVedBrukAvEgenBil) er oppgitt) eller
                      (fastsatt(bompengeutgift.beloep) er oppgitt) eller
                      (fastsatt(fergeutgift.beloep) er oppgitt)) og 
                      ikke((fastsatt(reiseFraAdresse) er oppgitt) og (fastsatt(reiseFraSted) er oppgitt) og
                           (fastsatt(reiseTilAdresse) er oppgitt) og (fastsatt(reiseTilSted) er oppgitt)) }

                hvis(harOppgittarbeidsreiseMedMangelfullAdresse) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

