sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val partsnr = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .verdiFor { partsnummer }

         val harUtslagPaaKontroll = utslagPåKontroll("EIENDOM_UTLEIE_I_FJOR")        

         val harUtvidetVeiledningPaaUtleie = utvidetVeiledning
             .forForekomsterAv { utvidetveiledning }
             .forForekomsterAv { metadata }
             .hvorMinstEnForekomst {type.finnesBlant("UTLEIE_AV_FAST_EIENDOM", "UTLEIE_SEKUNDAERBOLIGMODELL") }

         val harSekundaerboligSelveidEllerBoligselskap = skattemelding
             .forForekomsterAv{grupper(formuesgrunnlagSelveidBolig, 
                                       formuesgrunnlagBoenhetIBoligselskap)}
             .hvorMinstEnForekomst { boligbruk erLik "sekundaerbolig" }

         val harSekundaerboligFlerboligbygg = skattemelding
             .forForekomsterAv{formuesgrunnlagFlerboligbygning}
             .forForekomsterAv{useksjonertBoenhet}
             .hvorMinstEnForekomst { boligbruk erLik "sekundaerbolig" }

        // kontrollen gir utslag dersom skattyter har sekundærbolig, men mangler utleie
        // Skal ikke gi utslag hvis skattepliktig har fått produsert utvidet veileding (og vist det eller et tilhørende tips) 
        // skal heller ikke gi utslag dersom skattyter har utslag på EIENDOM_UTLEIE_I_FJOR
        //AB-test på skattepliktige som har tallet 2, 3, 4, 5, 6, 7, 8, 9 i 7. siffer i partsnummeret
        skattemelding
            .utslagFor {
               val harUtleieSjablongbehandlet = forForekomsterAv{grupper(sjablongbehandletUtleie,
                                                                         sjablongOgRegnskapsbehandletUtleie)
               }
                  .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) erStørreEnn 0}

               val harUtleieRegnskapsbehandlet = forForekomsterAv{grupper(regnskapsbehandletUtleie,
                                                                          sjablongOgRegnskapsbehandletUtleie) 
               }
                  .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep) erStørreEnn 0}    

            hvis(
                (partsnr.tegnFraIndeks(7).finnesBlant("2","3","4","5","6","7","8","9")) og
                ikke(harUtslagPaaKontroll) og
                ikke(harUtvidetVeiledningPaaUtleie) og
                (harSekundaerboligSelveidEllerBoligselskap eller harSekundaerboligFlerboligbygg) og
                ikke(harUtleieSjablongbehandlet) og
                ikke(harUtleieRegnskapsbehandlet)
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
            }
        }
        }

