        val harSkattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { type erLik "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet" }

        // kontrollen gir utslag dersom skattyter har særskilt skatteplikt på lønn fra den norske stat hele eller deler av året
        // og har en eller annen form for gjeldsrente eller gjeld
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val harGjeldEllerGjeldsrenter = forForekomsterAv(
                gruppe(
                    annenGjeld,
                    annenKapitalkostnad,
                    annetFinansprodukt,
                    kapitalisertFesteavgift,
                    konto,
                    skyldigRestskatt
                )
            )
                .hvorMinstEnForekomst {
                    (
                        (paaloepteRenter.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                            (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")
                        ) eller
                        (
                            (paaloepteRenterFraInkassoselskap.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                                (paaloepteRenterFraInkassoselskap.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")
                            ) eller
                        (
                            (betalteForsinkelsesrenter.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                                (betalteForsinkelsesrenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")
                            ) eller
