    .kontekst(ValideringSkattepliktig) {
        val harSkattepliktTilSvalbard = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { (personligSkattesubjekt.skattepliktTilSvalbard erLik "global") eller (doedsbo.skattepliktTilSvalbard erLik "global") }

        val primaerboligSelveidEllerBoligselskap = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }
            .verdierFor { internEiendomsidentifikator }
        }

        val primaerboligFlerboligbygg = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(formuesgrunnlagFlerboligbygning, useksjonertBoenhet))
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }
            .verdierFor { internEiendomsidentifikator }
        }

        // Kontrollen gir utslag dersom skattyter har globalskatteplikt på Svalbard
        // og har primærbolig i Norge, men ikke oppgitt utleie
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val harprimærboligINorge = forForekomsterAv(fastEiendom)
                .filtrer { (fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt) }
                .hvorMinstEnForekomst {
                    (internEiendomsidentifikator.finnesBlant(primaerboligSelveidEllerBoligselskap)) eller
                        (internEiendomsidentifikator.finnesBlant(primaerboligFlerboligbygg))
                }

            hvis(
                harSkattepliktTilSvalbard og
                    harprimærboligINorge og
                    ikke(
                        gruppe(
                            sjablongbehandletUtleie,
                            regnskapsbehandletUtleie,
                            sjablongOgRegnskapsbehandletUtleie
                        ) er oppgitt
                    )
            ) {
                betjeningsstrategiSettesTil(ikke, merknadTips, 1)
            }
        }
        }
    }

