           .hvorMinstEnForekomst{fastsatt(inntektsaar2022.fradragsberettigetBeloep.beloepSomTall) er myndighetsfastsatt}

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val harkravPaaSaerfradragSykdomsutgifterIkontrolliste = finnesIKontrolliste(
            "kontrolliste - saerfradrag store sykdomsutgifter",
             listeAv(partsnr)
            )

      // kontrollen gir utslag og merknad dersom skattyter fikk endret særfradrag for store sykdomsutgifter ved myndighetsfastsetting i fjor og krever også særfradrag i år
      //     eller skattepliktig krever særfradrag for store sykdomsutgifter med over kr 20 000 (før reduksjon til 67%)
      // uten å ha lagt ved dokumentasjon
      // skattyter må ligge på listen over de som har rett på å kreve fradraget for å få merknaden
       skattemelding
            .forForekomsterAv{saerfradragForStoreSykdomskostnader}
            .utslagFor {
                val harKrevdFradragForsykdomsutgifterIAar =
                    hvorMinstEnForekomst { fastsatt(rettTilAaKreveSaerfradrag) erLik true }

                val harVedleggSykdomsutgifter =
                    hvorMinstEnForekomst { fastsatt(rettTilAaKreveSaerfradrag) har vedleggPåFelt }

                val sumKravOmSaerfradrag = summer { fastsatt(sumSykdomskostnad.beloep) }

                hvis(harkravPaaSaerfradragSykdomsutgifterIkontrolliste og
                    ikke(harVedleggSykdomsutgifter) og
                    ((saerfradragStoreSykdomsutgifterMyndighetsfastsattIFjor og harKrevdFradragForsykdomsutgifterIAar) eller
                     (sumKravOmSaerfradrag erStørreEnn 20_000))
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon
                    )
                }
            }
        }

