sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .verdiFor { partsnummer }

        val eiendommerEidAvSkattyter = skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer { ((fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt)) og 
                       (fastsatt(eierandel) erStørreEnn 0)}
            .verdierFor { internEiendomsidentifikator }

        val identifikatorer = skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer {
                ((fastsatt(landkode) erLik "NO") eller (fastsatt(landkode) ikkeEr oppgitt)) og
                (fastsatt(internEiendomsidentifikator) er oppgitt)
            }
            .verdierFor { partsnr + ":" + sergEiendomsidentifikator }    

        // Kontrollen gir utslag på tomter i kontrolliste som benyttes som fritidseiendommer
        // hvor skattyter har eierandel større enn 0
        // Gir merknad i SME
        skattemelding
            .forForekomsterAv{formuesgrunnlagTomt}
            .utslagFor{

              val tomtFinnesBlantEideEiendommer = hvorMinstEnForekomst{fastsatt(internEiendomsidentifikator).finnesBlant(eiendommerEidAvSkattyter)}

              val tomtBenyttetSomFritidsboligFinnesIListe = finnesIKontrolliste(
                "Tomter benyttet som fritidseiendom",
                identifikatorer)    

                hvis(tomtFinnesBlantEideEiendommer og
                     tomtBenyttetSomFritidsboligFinnesIListe) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harTrygdefritakHelsedelOgIkkeSaerskiltSkattepliktISkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .filtrer { personligSkattesubjekt.fritakForTrygdeavgiftsplikt erLik "fritakForHelsedel" }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst {
                type.finnesIkkeBlant(
                    "kildeskattepliktPaaLoenn",
                    "kildeskattepliktPaaPensjon",
                    "skattepliktSomUtenrikstjenestemann",
                    "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato",
                    "skattepliktAvNaeringsdriftEiendomMv"
                )
            }

        val summenAvTrygdeavgift = beregnetSkattDokument
            .summer { beregnetSkatt.skattOgAvgift.sumTrygdeavgift.beloep }

        //kontrollen gir utslag dersom skattyter har trygdefritak helsedel for hele året
        //og skattyter IKKE har egenfastsatt trygdefritak (en av delene) på minst en lønn eller en pensjon
        //og beregnet trygdeavgift i beregnet skatt er større enn 0
        //skal ikke gi utslag på KPL, KPP, skatteplikt som utenrikstjenestemann eller av næringsdrift, eiendom mv
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val harOppgittTrygdefriLoenn = forForekomsterAv(arbeidsgiver)
                .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                .hvorMinstEnForekomst {
                    (
                        fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattepliktigUtenTrygdeavgiftsplikt"
                        )
                        ) og
                        (ikke(
                            grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                beloepSomGruppe.trygdeEllerSkattemessigUnntak
                            )
                        ))
                }

            val harOppgittTrygdefriPensjon = forForekomsterAv(pensjonsinntekt)
                .forForekomsterAv(perMaaned)
                .hvorMinstEnForekomst {
                    (
                        fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattepliktigUtenTrygdeavgiftsplikt"
                        )
                        ) og
                        (ikke(
                            grunnlag(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak
                            )
                        ))
                }



            hvis(
                harTrygdefritakHelsedelOgIkkeSaerskiltSkattepliktISkatteplikt og
                    (ikke(harOppgittTrygdefriLoenn) og ikke(harOppgittTrygdefriPensjon)) og
                    (summenAvTrygdeavgift erStørreEnn 0)
            )
            {
                betjeningsstrategiSettesTil(
                    ikke, merknadITopp
                )
            }
        }
        }
    }

