sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har oppgitt trygde- eller skattemessig unntak på lønn eller pensjon
        // men ikke har lagt til endringsårsak fritakDelvisFritakForTrygdeavgift eller opphoerTrygdemedlemskap på lønn eller pensjon
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, pensjonsinntekt))
            .filtrer {
                forForekomsterAv(aarsakTilEndring)
                    .harIngenForekomsterDer {
                        fastsatt(aarsak).finnesBlant(
                            "fritakDelvisFritakForTrygdeavgift",
                            "opphoerTrygdemedlemskap"
                        )
                    }
            }
            .utslagFor {
                val loennSomErOppgittMedTrygdemessigUnntak =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .filtrer { fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt }
                        .hvorMinstEnForekomst {
                            (
                                fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattepliktigUtenTrygdeavgiftsplikt",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                ) og
                                (grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                        }

                val pensjonSomErOppgittMedTrygdemessigUnntak = forForekomsterAv(perMaaned)
                    .filtrer { fastsatt(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt }
                    .hvorMinstEnForekomst {
                        (
                            fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattepliktigUtenTrygdeavgiftsplikt",
                                "skattefriUtenTrygdeavgiftsplikt"
                            )
                            ) og
                            (grunnlag(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                    }

                hvis(ikke(harInnsending) og loennSomErOppgittMedTrygdemessigUnntak eller pensjonSomErOppgittMedTrygdemessigUnntak) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harKrevdKreditfradragPaaInntektEllerFormue = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forAlleForekomster
            .hvorMinstEnForekomst { metodeVedDobbeltbeskatning erLik "kredit" }
        }

        // kontrollen gir utslag og merknad dersom  det er kreditfradrag til fremføring fra en av de 2 foregående årene
        // gir IKKE utslag dersom skattyter har lagt til kredit som metod på minst en inntekt eller formue
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(kravOmKreditFradragFremfoertUtenlandskSkatt)
            .utslagFor {
                val harFremfoertbartKreditfradragFra2foregaaendeAar = forForekomsterAv(perInntektsaar)
                    .hvorMinstEnForekomst {
                        ((inntektsaar erLik 2020) eller (inntektsaar erLik 2021)) og
                            ((fremfoertUtenlandskFormuesskatt.beloepSomTall erStørreEnn 0) eller (fremfoertUtenlandskInntektsskatt.beloepSomTall erStørreEnn 0))
                    }

                hvis(
                    harFremfoertbartKreditfradragFra2foregaaendeAar og ikke(
                        harKrevdKreditfradragPaaInntektEllerFormue
                    )
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
        }
    }

