            .forForekomsterAv{skatteplikt}
            .forForekomsterAv{personligSkattesubjekt.saerskiltSkatteplikt}
            .hvorMinstEnForekomst { type erLik "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato" }

        //kontrollen gir utslag dersom skattyter har særskilt skatteplikt som utenriks tjenestemann deler av eller hele året i skatteplikt
        //og har utbytte på aksjer /verdipapirfond
        //utbyttet skal også være satt til skattefriUtenTrygdeavgiftsplikt
        skattemelding
            .utslagFor {
                val harAksjeUtbytte = forForekomsterAv{
                    grupper(
                        aksjeIAksjonaerregisteret,
                        aksjeIkkeIAksjonaerregisteret,
                        verdipapirfond
                    )
                }
                    .hvorMinstEnForekomst {
                        (skattepliktigUtbytte.beloep erStørreEnn 0) og
                        (skattepliktigUtbytte.trygdeEllerSkattemessigUnntak erLik "skattefriUtenTrygdeavgiftsplikt")
                    }


                hvis(erUtenriksTjenestemann og harAksjeUtbytte)
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erUtenriksTjenestemann = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { type erLik "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato" }

        val harOppholdUnderYrkesOgTjenestereiseIUtlandet = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(oppholdUnderYrkesOgTjenestereise)
            .hvorMinstEnForekomst { (landkode er oppgitt) og (landkode erUlik "NO") }
        }

        val harPendleroppholdIUtlandet = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pendleropphold)
            .hvorMinstEnForekomst { (landkode er oppgitt) og (landkode erUlik "NO") }
        }

        //kontrollen gir utslag dersom skattyter har særskilt skatteplikt som utenriks tjenestemann i skatteplikt
        //og har kort for oppholdUnderYrkesOgTjenestereise eller pendleropphold med opphold i utlandet,
        //eller har kort for arbeidsreiser
        //men ikke har fylt ut andel av totale merkostnader i utlandet i kort for arbeidsoppholdUtenforHjemmet
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsoppholdUtenforHjemmet, reiserIArbeidsforhold))
            .utslagFor {

                val harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetMerkostnader = hvorMinstEnForekomst {
                    (fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomGruppe.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                        (fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomGruppe.heravAndelTilordnetSaerskiltSkattepliktVedArbeidsoppholdIUtlandet.beloepSomTall erLik 0)
                }

                val harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetArbeidsreise = hvorMinstEnForekomst {
                    (reisefradragSomGruppe.fradragsberettigetBeloep.beloepSomTall erStørreEnn 0) og
                        (reisefradragSomGruppe.heravAndelTilordnetSaerskiltSkattepliktVedArbeidsoppholdIUtlandet.beloepSomTall erLik 0)
                }

                hvis(
                    erUtenriksTjenestemann og
                        (((harOppholdUnderYrkesOgTjenestereiseIUtlandet eller harPendleroppholdIUtlandet) og harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetMerkostnader) eller
                            harIkkeFyltUtAndelAvTotalenSomGjelderUtlandetArbeidsreise)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .hvis({ skattepliktGjeldende
              .forForekomsterAv { skatteplikt }
              .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }  
    }) 
    .importer(SLUTTDATO_I_INNTEKTSAARET) {
      (D_sluttdatoIInntektsaaret) ->

        val sluttdatoIInntektsaaret = D_sluttdatoIInntektsaaret

        val harSaerskiltSkattepliktUtenrikstjenestemann = skattepliktGjeldende
           .forForekomsterAv { skatteplikt }
           .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
           .hvorMinstEnForekomst { type erLik "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato" }

        val uteITjenesteVedSluttenAvAaret = periodelager
           .forForekomsterAv { perioder }
           .forForekomsterAv { arbeidsforholdsperiode }
           .hvorMinstEnForekomst{(tilOgMedDato ikkeEr oppgitt) eller (tilOgMedDato erEtter sluttdatoIInntektsaaret) }

        val eiendomIUtlandet = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(fastEiendom)
            .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
            .verdierFor { internEiendomsidentifikator }
        }


        // kontrollen skal kun kjøre hvis skattyter har begrenset skatteplikt
        // kontrollen gir utslag dersom skattyter har særskilt skatteplikt som utenriks tjenestemann
        //og sluttdato for arbeidsoperiode enten ikke er oppgitt eller den er senere enn 31.12. i inntektsåret (er ute i tjeneste ved utgangen av året)
        // og skattyter har egenfastsatt formue som er skattepliktig - innskudd, kjøretøy, eiendom i utlandet
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(
            gruppe(
                kjoeretoey,
                konto,
                formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                formuesgrunnlagBoenhetIBoligselskap,
                formuesgrunnlagBorett,
                formuesgrunnlagEgenFritaksbehandletBolig,
                formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                sti(formuesgrunnlagFlerboligbygning, useksjonertBoenhet),
                formuesgrunnlagFlerboligbygning,
                formuesgrunnlagGaardsbruk,
                formuesgrunnlagIkkeUtleidNaeringseiendomIUtlandet,
                formuesgrunnlagRegnskapsbehandletBolig,
                formuesgrunnlagRegnskapsbehandletFritidseiendom,
                formuesgrunnlagSelveidBolig,
                formuesgrunnlagSelveidFritidseiendom,
                formuesgrunnlagSkogeiendomIUtlandet,
                formuesgrunnlagTomt,
                formuesgrunnlagUtleidFlerboligbygningIUtlandet,
                formuesgrunnlagUtleidNaeringseiendom
            )
        )
            .utslagFor {

                val harOppgittFormueKjoeretoey = filtrer {
                    fastsatt(formuesverdi.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                    )
                }
                    .hvorMinstEnForekomst {
                        (fastsatt(formuesverdi.beloepSomTall) erStørreEnn 0) og
                            (grunnlag(formuesverdi.beloepSomTall) erLik 0)
                    }

                val harOppgittFormueInnskudd = filtrer {
                    fastsatt(innskudd.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                    )
                }
                    .hvorMinstEnForekomst {
                        (fastsatt(innskudd.beloepSomTall) erStørreEnn 0) og
                            (grunnlag(innskudd.beloepSomTall) erLik 0)
                    }

                val harOppgittFormueEiendomIUtlandet = filtrer {
                    (fastsatt(formuesverdiForFormuesandel.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                        "skattefriMedTrygdeavgiftspliktForHelsedel",
                        "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                        "skattefriMedTrygdeavgiftsplikt",
                        "skattefriKildeskattPaaPensjon",
                        "skattefriUtenTrygdeavgiftsplikt",
                        "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                    )) og
                        (internEiendomsidentifikator.finnesBlant(eiendomIUtlandet))
                }
                    .hvorMinstEnForekomst {
                        (fastsatt(formuesverdiForFormuesandel.beloepSomTall) erStørreEnn 0) og
                            (grunnlag(formuesverdiForFormuesandel.beloepSomTall) erLik 0)
                    }

                hvis(
                    harSaerskiltSkattepliktUtenrikstjenestemann og
                        uteITjenesteVedSluttenAvAaret og
                        (harOppgittFormueKjoeretoey eller harOppgittFormueInnskudd eller harOppgittFormueEiendomIUtlandet)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val erUtenriksTjenestemannHeleAaret = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .filtrer{(fastsatt(personligSkattesubjekt.tolvdelVedArbeidsoppholdIUtlandet) erLik 12) eller 
                     (fastsatt(personligSkattesubjekt.tolvdelVedArbeidsoppholdIUtlandet) ikkeEr oppgitt)}
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { type erLik "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato" }

        //kontrollen gir utslag dersom skattyter har særskilt skatteplikt som utenriks tjenestemann ved utgangen av året (12/12 ved arbeidsopphold i utlandet)
        //og har en eller annen form for gjeld

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val harGjeld = forForekomsterAv(
                gruppe(
                    annenGjeld,
                    annetFinansprodukt,
                    kapitalisertFesteavgift,
                    konto,
                    skyldigRestskatt
                )
            )
                .hvorMinstEnForekomst {
                    ((gjeldsbeloep.beloepSomTall erStørreEnn 0) og (gjeldsbeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                        ((gjeldPaaAnnetFinansprodukt.beloepSomTall erStørreEnn 0) og (gjeldPaaAnnetFinansprodukt.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                        ((gjeldsfradragForKapitalisertFesteavgift.beloepSomTall erStørreEnn 0) og (gjeldsfradragForKapitalisertFesteavgift.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                        ((gjeld.beloepSomTall erStørreEnn 0) og (gjeld.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                        ((beloepSomGruppe.beloepSomTall erStørreEnn 0) og (beloepSomGruppe.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))
                }


            hvis(erUtenriksTjenestemannHeleAaret og harGjeld)
            {
                betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
            }
        }
        }
    }

sakstype(Personlig)
      .kontekst(ValideringSkattepliktig) {
           val datoIInntektsaar = KonstantTekstFelt("2023-01-01").dato

           val harBegrensetSkattepliktIInntektsaaret = skattepliktGjeldende
              .forForekomsterAv { skatteplikt }
              .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

            val harBegrensetSkattepliktFjoraaret = fjoraaretsSkattepliktGjeldende
              .forForekomsterAv(inntektsaar2022.skatteplikt)
              .hvorMinstEnForekomst { inntektsaar2022.personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

             val harSaerskiltSkattepliktIInntektsaaret = skattepliktGjeldende
              .forForekomsterAv { skatteplikt }
              .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
              .hvorMinstEnForekomst {
                  type.finnesBlant(
                      "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet",
                      "skattepliktEtterPetroleumsskatteloven",
                      "skattepliktSomSjoemann",
                      "skattepliktAvNaeringsdriftEiendomMv",
                      "kildeskattepliktPaaLoenn",
                      "kildeskattepliktPaaPensjon",
                      "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato"
                  )
              }

            val harSaerskiltSkattepliktIFjor = fjoraaretsSkattepliktGjeldende
              .forForekomsterAv(inntektsaar2022.skatteplikt)
              .forForekomsterAv(sti(inntektsaar2022.personligSkattesubjekt,inntektsaar2022.saerskiltSkatteplikt))
              .hvorMinstEnForekomst {
                  inntektsaar2022.type.finnesBlant(
                      "skattepliktPaaLoennFraDenNorskeStatOpptjentIUtlandet",
                      "skattepliktEtterPetroleumsskatteloven",
                      "skattepliktSomUtenrikstjenestemann",
                      "skattepliktSomSjoemann",
                      "skattepliktAvNaeringsdriftEiendomMv",
                      "kildeskattepliktPaaLoenn",
                      "kildeskattepliktPaaPensjon",
                      "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato"
                  )
              }

           val harUtflyttingsskattIFjor = fjoraaretsSkattepliktGjeldende
              .hvorMinstEnForekomst {inntektsaar2022.spesifikasjonAvUtflyttingOgUtflyttingsskatt er oppgitt}

           val harKravOmBostedEtterSkatteavtaleIAnnetLandIFjor = fjoraaretsSkattepliktGjeldende
              .hvorMinstEnForekomst {inntektsaar2022.tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale er oppgitt}

            val harKravOmSkattemessigEmigrasjonIFjor = fjoraaretsSkattepliktGjeldende
              .hvorMinstEnForekomst {inntektsaar2022.tilleggsinformasjonVedSkattemessigUtflytting er oppgitt}

            val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_AVTALEBOSTED_SAMME_DATO")

          //Det finnes en forekomst av AndreForhold -> TilleggsinformasjonVedSkattemessigUtflytting og
          // flyttedato i dette kortet er 1.1. i inntektsåret
          //Skal ikke gi utslag på skattepliktig som har begrenset skatteplikt eller særskatteplikt i året før inntektsåret
          // og som fortsatt har slik skatteplikt i inntektsåret
          //Skal ikke gi utslag på skattepliktig som i året før inntektsåret har fylt ut ett eller flere av følgende kort: "Utflyttingsskatt", 
          // "Krav om bosted etter skatteavtale i annet land" eller "Krav om skattemessig emigrasjon"
          // skal ikke gi utslag hvis utslag på EMIGRASJON_AVTALEBOSTED_SAMME_DATO

          skattemelding
              .fokusområde("Fokusområde")
              .forForekomsterAv { tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale }
              .utslagFor {
                val startdatoOppgittOgEtterDatoIInntektsaar = hvorMinstEnForekomst {
                  (startdatoForBosattIUtlandEtterSkatteavtale er oppgitt) og 
                  	(startdatoForBosattIUtlandEtterSkatteavtale erLik datoIInntektsaar)
             }
             hvis( ikke(harUtslagPaaKontroll) og 
                  ikke(harBegrensetSkattepliktIInntektsaaret) og ikke(harBegrensetSkattepliktFjoraaret) og
                  ikke(harSaerskiltSkattepliktIInntektsaaret) og ikke(harSaerskiltSkattepliktIFjor) og
                  ikke(harUtflyttingsskattIFjor) og ikke(harKravOmBostedEtterSkatteavtaleIAnnetLandIFjor) og
                  ikke(harKravOmSkattemessigEmigrasjonIFjor) og startdatoOppgittOgEtterDatoIInntektsaar) 

            {
               betjeningsstrategiSettesTil(ikke, merknadStandard)
          }
        }
    }

