                        .summer { skattepliktigGevinstVedRealisasjon.beloepSomTall }

                val latentGevinsDeltakerfastsetting = summer { skattepliktigLatentGevinstVedRealisasjonAvAndel }

                val latentTapDeltakerfastsetting = summer { skattepliktigLatentTapVedRealisasjonAvAndel }

                val realisertGevinsOpsjonerIArbeidsforhold = forForekomsterAv(realisasjonAvOpsjon)
                    .summer { skattepliktigGevinstVedRealisasjon.beloepSomTall }

                val realisertTapOpsjonerIArbeidsforhold = forForekomsterAv(realisasjonAvOpsjon)
                    .summer { skattepliktigGevinstVedRealisasjon.beloepSomTall }

                val latentGevinsOpsjonerIArbeidsforhold =
                    summer { skattepliktigLatentGevinstVedRealisasjonAvOpsjon }

                val latentTapOpsjonerIArbeidsforhold = summer { skattepliktigLatentTapVedRealisasjonAvOpsjon }


                hvis(
                    ((latentGevinsFinansprodukt erStørreEnn 0) og (realisertGevinsFinansprodukt erStørreEnn latentGevinsFinansprodukt)) eller
                        ((latentTapFinansprodukt erStørreEnn 0) og (realisertTapFinansprodukt erStørreEnn latentTapFinansprodukt)) eller
                        ((latentGevinsDeltakerfastsetting erStørreEnn 0) og (realisertGevinsDeltakerfastsetting erStørreEnn latentGevinsDeltakerfastsetting)) eller
                        ((latentTapDeltakerfastsetting erStørreEnn 0) og (realisertTapDeltakerfastsetting erStørreEnn latentTapDeltakerfastsetting)) eller
                        ((latentGevinsOpsjonerIArbeidsforhold erStørreEnn 0) og (realisertGevinsOpsjonerIArbeidsforhold erStørreEnn latentGevinsOpsjonerIArbeidsforhold)) eller
                        ((latentTapOpsjonerIArbeidsforhold erStørreEnn 0) og (realisertTapOpsjonerIArbeidsforhold erStørreEnn latentTapOpsjonerIArbeidsforhold))
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }
    }

