sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskatt)
        }

        val latentGevinsFinansprodukt = utgåtteSkattemeldingFelter {
            aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                .summer { skattepliktigLatentGevinstVedRealisasjonAvAksjeOgAnnetFinansprodukt }
        }

        val latentTapFinansprodukt = utgåtteSkattemeldingFelter {
            aksjeOgAnnetFinansproduktOmfattetAvUtflyttingsskattForekomster
                .summer { skattepliktigLatentTapVedRealisasjonAvAksjeOgAnnetFinansprodukt }
        }

        val andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskatt)
        }

        val latentGevinsDeltakerfastsetting = utgåtteSkattemeldingFelter {
            andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                .summer { skattepliktigLatentGevinstVedRealisasjonAvAndel }
        }

        val latentTapDeltakerfastsetting = utgåtteSkattemeldingFelter {
            andelISelskapMedDeltakerfastsettingOmfattetAvUtflyttingsskattForekomster
                .summer { skattepliktigLatentTapVedRealisasjonAvAndel }
        }

        val opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(opsjonerIArbeidsforholdOmfattetAvUtflyttingsskatt)
        }

        val latentGevinsOpsjonerIArbeidsforhold = utgåtteSkattemeldingFelter {
            opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                .summer { skattepliktigLatentGevinstVedRealisasjonAvOpsjon }
        }

        val latentTapOpsjonerIArbeidsforhold = utgåtteSkattemeldingFelter {
            opsjonerIArbeidsforholdOmfattetAvUtflyttingsskattForekomster
                .summer { skattepliktigLatentTapVedRealisasjonAvOpsjon }
        }

        val sumLatentGevinst = latentGevinsFinansprodukt + latentGevinsDeltakerfastsetting + latentGevinsOpsjonerIArbeidsforhold

        val sumLatentTap = latentTapFinansprodukt + latentTapDeltakerfastsetting + latentTapOpsjonerIArbeidsforhold  

        val EOESLandkoder = aktiveLandkoder_2023
            .forForekomsterAv { kode }
            .filtrer { kodetillegg.medlemAvEoes erLik "Ja" }
            .verdierFor { tekniskNavn }


        // Kontrollen gir utslag dersom oppgitt latent gevinst/tap på kortene under tilsammen er større enn 500 000
        //    Aksjer og andre finansprodukter omfattet av utflyttingsskatt eller
        //    Andel i selskap med deltakerfastsetting omfattet av utflyttingsskatt eller
        //    Opsjoner i arbeidsforhold omfattet av utflyttingsskatt    
        // Gir bare utslag dersom oppgitt land i kort for Utflyttingsskatt er innenfor EØS
        // og skattyter ikke har valgt «Jeg krever utsatt beskatning» under «Krav om utsatt beskatning»
        // Merknaden vises på kortet for Utflyttingsskatt
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(spesifikasjonAvUtflyttingOgUtflyttingsskatt)
            .utslagFor {

                val harKortForUtflyttingsskatt = hvorMinstEnForekomst { land er oppgitt }

                val utflyttingsskattTilEOESLandIkkeUtsattBeskatning = hvorMinstEnForekomst {
                    (land.finnesBlant(EOESLandkoder)) og
                        (kravOmUtsattBeskatning erUlik true)
                }


                hvis(
                    harKortForUtflyttingsskatt og
                        utflyttingsskattTilEOESLandIkkeUtsattBeskatning og
                        ((sumLatentGevinst erStørreEnn 500_000) eller (sumLatentTap erStørreEnn 500_000))
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }
    }

