                    (fastsatt(andelAvPaaloepteRenter.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(andelAvBetalteMisligholdteRenter.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(gevinstValutalaan.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(gjeld.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(innskudd.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(opptjenteRenter.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(tapValutalaan.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(tilbakefoertRente.beloepSomTall) ikkeEr oppgitt)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erGlobalskattepliktig = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

        // kontrollen gir utslag dersom skattyter er globalskattepliktig
        // og har valgt "ikke skattepliktig" på lønnsinntekt av type LONN_UTL eller LONN med landkode ulik NO
        // og IKKE har endringsårsak
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .filtrer {
                (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") og
                    (fastsatt(aarsakTilEndring) ikkeEr oppgitt)
            }
            .utslagFor {
                val harOppgittSkattefriLoenn = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .filtrer { fastsatt(behandlingsart).finnesBlant("LONN", "LONN_UTL") }
                    .hvorMinstEnForekomst {
                        fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattefriMedTrygdeavgiftsplikt",
                            "skattefriKildeskattPaaPensjon",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                        )
                    }

                hvis(erGlobalskattepliktig og harOppgittSkattefriLoenn) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har nullstilt utenlandske lønn (med landkode) av type LONN eller LONN_UTL med grunnlag(et utslag pr arbeidsgiver)
        // gir ikke utslag dersom lønnen er merket med skattefritt unntak, da det gir utslag på annen kontroll
        // gir ikke utslag dersom det er utslag på UTL_LONN_SATT_TIL_0_MERKNAD2
        val harUtslagPaaKontroll = utslagPåKontroll("UTL_LONN_SATT_TIL_0_MERKNAD2")
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .filtrer { (grunnlag(landkode) er oppgitt) og ikke(grunnlag(landkode) erLik "NO") }
            .utslagFor {
                val forekomsterSomGirUtslag = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (grunnlag(behandlingsart).finnesBlant("LONN", "LONN_UTL")) og
                            (
                                grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                ) og
                            (grunnlag(beloepSomGruppe.beloepSomTall) erStørreEnn 0) og
                            ikke(fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0)
                    }

                hvis(
                    ikke(harInnsending) og
                        ikke(harUtslagPaaKontroll) og
                        (forekomsterSomGirUtslag)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

