        skattemelding
            .forForekomsterAv{verdipapirfond}
            .utslagFor {
                val harDifferanseTapAksjedel = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloep) erLik 0) og
                        ((fastsatt(skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloep) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloep))
                                erStørreEnnEllerLik 50_000)
                            }

                val harDifferanseTapRentedel = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloep) erLik 0) og
                        ((fastsatt(skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloep) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloep))
                                 erStørreEnnEllerLik 50_000)
                         }

                 val harVedleggAksjedel = hvorMinstEnForekomst{skattepliktigTapVedRealisasjonAvAndelIAksjedel.beloepUtenHensynTilValgtPrioritertFradragstype.beloepIValuta har vedleggPåFelt}

                  val harVedleggRentedel = hvorMinstEnForekomst{skattepliktigTapVedRealisasjonAvAndelIRentedel.beloepUtenHensynTilValgtPrioritertFradragstype.beloepIValuta har vedleggPåFelt}

                hvis(
                    ikke(harInnsending) og (ikke(harVedleggAksjedel) og harDifferanseTapAksjedel) eller
                        (ikke(harVedleggRentedel) og harDifferanseTapRentedel)
