sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val sivilstatusFjoraaret = fjoraaretsSkattepliktGjeldende
            .forForekomsterAv(inntektsaar2022.skatteplikt)
            .forForekomsterAv(inntektsaar2022.personligSkattesubjekt.sambeskatningMedAnnenPart)
            .verdiFor { inntektsaar2022.sambeskatningsrolle }

        val sivilstatusIAar = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.sambeskatningMedAnnenPart }
            .verdiFor { sambeskatningsrolle }

        val primaerboligSelveidEllerBoligselskapFjoraaret = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
            .filtrer { fastsatt(inntektsaar2022.boligbruk) erLik "primaerbolig" }
            .verdiFor { fastsatt(inntektsaar2022.internEiendomsidentifikator) }
        }

        val primaerboligFlerboligbyggFjoraaret = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(formuesgrunnlagFlerboligbygning, useksjonertBoenhet))
            .filtrer { fastsatt(inntektsaar2022.boligbruk) erLik "primaerbolig" }
            .verdiFor { fastsatt(inntektsaar2022.internEiendomsidentifikator) }
        }

        val primaerboligSelveidEllerBoligselskapIAar = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }
            .verdierFor { fastsatt(internEiendomsidentifikator) }
        }

        val primaerboligFlerboligbyggIAar = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(formuesgrunnlagFlerboligbygning, useksjonertBoenhet))
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }
            .verdierFor { fastsatt(internEiendomsidentifikator) }
        }

        val studielaanFjoraaret = fjoraaretsSkattemelding
            .forForekomsterAv(inntektsaar2022.konto)
            .filtrer{ fastsatt(inntektsaar2022.organisasjonsnummerSomGruppe.organisasjonsnummerSomTekst) erLik "960885406" }
            .summer{ inntektsaar2022.gjeldSomGruppe.beloepSomTall }

        val studielaanIAar = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(konto)
            .filtrer { fastsatt(organisasjonsnummerSomGruppe.organisasjonsnummerSomTekst) erLik "960885406" }
            .summer { fastsatt(gjeld.beloepSomTall) }
        }

        val sumInntekt = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(summertSkattegrunnlagForVisning)
            .forForekomsterAv(sumInntekterIAlminneligInntektFoerFordelingsfradrag_SKVIS)
            .summer { fastsatt(beloep_SKVIS) }
        }

        val sumInnskudd = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(konto)
            .summer { fastsatt(innskudd.beloepSomTall) }
        }


        // kontrollen gir utslag dersom de har rett til skattebegrensning §17.4, og har fastsatt at de ønskes å vurderes for liten skatteevne
        //skal bare gi utslag dersom skattyter har
        //endret sivilstatus(gift/skilt) fra forrige år
        //eller skattyter har flyttet i forhold til forrige år
        //eller har økt studielånet fra forrige år med mer enn 40 000 (statens lånekasse - orgnr: 960885406)
        //eller har inntekt på 260 000 eller mer
        //eller har  innskudd på 80 000 eller mer
        //og ikke har lagt ved vedlegg i kortet for skattebegrensning
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("fokusområde")
            .forForekomsterAv(skattebegrensningParagraf174)
            .utslagFor {

                val harVedlegg = vurderesForSkattebegrensningParagraf174LitenSkatteevne har vedlegg

                val harRettPaaOgSkalVurderesForSkattebegrensning = hvorMinstEnForekomst {
                    (fastsatt(vurderesForSkattebegrensningParagraf174LitenSkatteevne) erLik true) og
                        (grunnlag(rettTilSkattebegrensningParagraf174LitenSkatteevneForegaaendeAar) erLik true)
                }

                hvis(
                    ikke(harInnsending) og
                        ikke(harVedlegg) og
                        harRettPaaOgSkalVurderesForSkattebegrensning og
                        ((sivilstatusFjoraaret erUlik sivilstatusIAar) eller
                            ((primaerboligSelveidEllerBoligselskapIAar.antall erStørreEnn 0) og (primaerboligSelveidEllerBoligselskapFjoraaret.finnesIkkeBlant(
                                primaerboligSelveidEllerBoligselskapIAar
                            ))) eller
                            ((primaerboligFlerboligbyggIAar.antall erStørreEnn 0) og (primaerboligFlerboligbyggFjoraaret.finnesIkkeBlant(
                                primaerboligFlerboligbyggIAar
                            ))) eller
                            ((studielaanIAar - studielaanFjoraaret) erStørreEnn 40_000) eller
                            (sumInntekt erStørreEnnEllerLik 260_000) eller
                            (sumInnskudd erStørreEnnEllerLik 80_000))

                ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadDokumentasjon,skjulerSkatteberegning
                    )
                }
            }
        }
    }
