                   (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) erStørreEnnEllerLik (fastsatt(gjeldsbeloep.beloep) * 10.prosent))              
                }

                val harVedlegg = hvorMinstEnForekomst{paaloepteRenter.beloepUtenHensynTilValgtPrioritertFradragstype.beloepIValuta har vedleggPåFelt}

                hvis(ikke(harUtslagPaaKontroll) og
                     harOppgittAnnenGjeldStoerreEnnBeloepsgrense og
                     ikke(harVedlegg)) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

     // Kontrollen gir utslag dersom krever fradrag for annen gjeld og gitte ord for lån tilknyttet lånekassen forekommer i felt for id-nummer eller navn på kreditor
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{annenGjeld}
            .utslagFor {

                val harOppgittAnnenGjeldLaanekassen = hvorMinstEnForekomst{
                  ((fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloep) er oppgitt) eller (fastsatt(gjeldsbeloep.beloep) er oppgitt)) og
                  ((fastsatt(fordringshaver.navn).inneholder("lånekassen")) eller
                  (fastsatt(fordringshaver.navn).inneholder("lanekassen")) eller
                  (fastsatt(fordringshaver.navn).inneholder("låne kassen")) eller
                  (fastsatt(fordringshaver.navn).inneholder("lane kassen")) eller
                  (fastsatt(fordringshaver.navn).inneholder("studiegjeld")) eller
                  (fastsatt(fordringshaver.navn).inneholder("studie gjeld")) eller
                  (fastsatt(fordringshaver.navn).inneholder("studielån")) eller
                  (fastsatt(fordringshaver.navn).inneholder("studie lån")) eller
                  (fastsatt(fordringshaver.identifikator).inneholder("lånekassen")) eller
