    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt formue på aksjesparekonto er ulik grunnlag med 100 mill eller mer
        skattemelding
            .forForekomsterAv{aksjesparekonto}
            .utslagFor {
                val differanseFormueFoerVerdsettingsrabatt = summer{differanse(
                    fastsatt(verdiFoerVerdsettingsrabattForAksjedel.beloep),
                    grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloep)) }

                val differanseFormueKontanter = summer{differanse(
