                hvis((differanseFormueFoerVerdsettingsrabatt + differanseFormueKontanter) erStørreEnnEllerLik 100_000_000) {
                    betjeningsstrategiSettesTil(
                        hentesFraOppgavetypedefinisjonen, merknadStandard,
                        Avviksbeloep settesTil (differanseFormueFoerVerdsettingsrabatt + differanseFormueKontanter)
                    )
                }
            }
    }

