                                   utslagPåKontroll("EMIGRASJON_AVTALEBOSTED_SAMME_DATO")

         // hvis kort for avtalebosted under Andre forhold er registrert 
         // og skattyter ikke har oppgitt dokumentasjon i kortet
         // skal ikke gi utslag hvis skattepliktig har skatteplikt Begrenset
         // skal ikke gi utslag dersom skattyter har utslag på gitte kontroller
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale)
            .utslagForFørste {

                val kortForAvtalebostedErOppgitt =
                    hvorMinstEnForekomst { bosattILandEtterSkatteavtale er oppgitt }

                val harVedleggIKortForAvtalebosted =
                    hvorMinstEnForekomst { bosattILandEtterSkatteavtale har vedlegg }

                hvis(
                    ikke(harBegrensetSkatteplikt) og
                        ikke(harUtslagPaaKontroll) og
