sakstype(Personlig) 
     .kontekst(ValideringSkattepliktig) 
     .importer(SKATTEPLIKTIG_LØNN ) 
      { 
       (D_skattepliktigLoenn) ->

        val bruttoLoennUtgiftsgodtgjoerelseMv = skattemelding.D_skattepliktigLoenn()
            .summer { fastsatt(beloep) } 

              // Fastsatt minus grunnlag på "Annen_Gjeld/Rentekostnader") > (40% av brutto lønn)). I tillegg må fradrag overstige 100 000
           skattemelding
              .forForekomsterAv{konto}
              .utslagFor {
                  val oekteRentekostnader = verdiFor{fastsatt(andelAvPaaloepteRenter.beloep) - 
                                                     grunnlag(andelAvPaaloepteRenter.beloep)}

                  val harVedlegg = hvorMinstEnForekomst{paaloepteRenter.beloepUtenHensynTilValgtPrioritertFradragstype.beloepIValuta har vedleggPåFelt}

                  hvis( ikke(harVedlegg) og
                     (oekteRentekostnader erStørreEnn 100_000) og
                     (oekteRentekostnader erStørreEnn (bruttoLoennUtgiftsgodtgjoerelseMv * 40.prosent))
                    )
                 {
                      betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, skjulerSkatteberegning)
                }
            }
    }

