        // Kontrollen gir utslag dersom skattyter har mindre enn 100 % i andel av lån og gjeldsrenter (og større enn 0)
        // og skattepliktig reduserer andel av lån og renter (i prosent)
        // gir IKKE utslag hvis det er utslag på en av de andre merknadene for redusert andel
      skattemelding
            .forForekomsterAv{konto}
            .utslagFor {
                val harPreutfyltProsentAndelMindreEnn100OgRedusert = hvorMinstEnForekomst {
                        (grunnlag(prosentandelAvGjeldInnskuddOgRente) erStørreEnn 0) og
                        (grunnlag(prosentandelAvGjeldInnskuddOgRente) erMindreEnn 100) og
                        (grunnlag(prosentandelAvGjeldInnskuddOgRente) erStørreEnn fastsatt(prosentandelAvGjeldInnskuddOgRente)) 
                    }

                hvis( ikke(harUtslagPaaAndreKontrollerForReduksjonAvAndelLaan) og
                    harPreutfyltProsentAndelMindreEnn100OgRedusert
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadBekreft, avhukingsboks)
                }
            }
        }

