    //utbytte under aksjer i aksjonærregistere med unntak utbytte omfattet av skatteavtale eller
    //utbytte under verdipapirfond unntak utbytte omfattet av skatteavtale 
    // og ikke har lagt til kortet Krav om bosted etter skatteavtale i annet land, under tema Andre forhold
        skattemelding
           .utslagFor {

            val harRenterMedUnntak = forForekomsterAv{konto}
               .hvorMinstEnForekomst {
                  fastsatt(opptjenteRenter.trygdeEllerSkattemessigUnntak) erLik
                    ("renteinntektOmfattetAvSkatteavtaleVedSkatteavtalebostedIAnnenStat")
                }

