                       (fastsatt(bankensNavn).inneholder("intrum")) eller
                       (fastsatt(bankensNavn).inneholder("alektum")) eller
                       (fastsatt(bankensNavn).inneholder("svea")) eller
                       (fastsatt(bankensNavn).inneholder("sergel")) eller
                       (fastsatt(bankensNavn).inneholder("kredinor")) eller
                       (fastsatt(bankensNavn).inneholder("Lowell ")) eller
                       (fastsatt(bankensNavn).inneholder("avida")) eller
                       (fastsatt(bankensNavn).inneholder("spk")) eller
                       (fastsatt(bankensNavn).inneholder("pensjonskasse")) eller
                       (fastsatt(bankensNavn).inneholder("pensjonkasse")) )
                }

                hvis(harOppgittNyKontoMedGjeldEllerGjeldsrenterINorge og
                    harGjeldskontoIUtkastMedMedlaantaker) {
