        // og eiendomstypen er noe annet enn en av de som er gyldige for utenlandske eiendommer
        //    borett
        //    egenFritaksbehandletBolig
        //    regnskapsbehandletBolig
        //    egenFritaksbehandletFritidseiendom
        //    regnskapsbehandletFritidseiendom
        //    tomt
        //    gaardsbruk
        //    skogeiendomIUtlandet
        //    ikkeUtleidNaeringseiendomIUtlandet
        //    annenFastEiendomInnenforInntektsgivendeAktivitet
        //    annenFastEiendomUtenforInntektsgivendeAktivitet
        //    utleieFlerboligbygningIUtlandet
        //    naeringseiendom (formuesgrunnlagUtleidNaeringseiendom)
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer{(grunnlag(poststedsnavnInternasjonalAdresse) ikkeEr oppgitt) og
                     (fastsatt(poststedsnavnInternasjonalAdresse) er oppgitt) og
                     (fastsatt(landkodeBeliggenhet) er oppgitt) og
                     (fastsatt(landkodeBeliggenhet) erUlik "NO")}     
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagFor {

                val harOppgittFeilTypeutenlandskEiendom = hvorMinstEnForekomst{fastsatt(eiendomstype).finnesBlant("boenhetIBoligselskap", "flerboligbygning", "ikkeUtleidNaeringseiendomINorge", "selveidBolig", "selveidFritidseiendom", "skogeiendomINorge")}


                hvis(harOppgittFeilTypeutenlandskEiendom) {
                    betjeningsstrategiSettesTil(
                        ikke, faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

    // Gir utslag hvis summen av vedlikehold og andre kostnader > kr 150 000
       skattemelding
       .forForekomsterAv{fastEiendom}
       .forForekomsterAv{fastEiendomSomUtleieobjekt}
       .utslagFor {

       val sumUtleiekostnader = 
           summer{fastsatt(vedlikeholdskostnadVedUtleie.beloep) +
                    fastsatt(annenKostnadVedUtleie.beloep)} 

       val harVedleggVedlikehold =
           hvorMinstEnForekomst{vedlikeholdskostnadVedUtleie.beloep har vedleggPåFelt} 

        val harVedleggAnnenKostnad =
           hvorMinstEnForekomst{annenKostnadVedUtleie.beloep har vedleggPåFelt}

       hvis(ikke(harVedleggVedlikehold) og ikke(harVedleggAnnenKostnad) og
             (sumUtleiekostnader erStørreEnn 150_000))
         {
                betjeningsstrategiSettesTil(ikke, merknadDokumentasjon)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harUtvidetVeiledningPaaUtleie = utvidetVeiledning
           .forForekomsterAv { utvidetveiledning }
           .forForekomsterAv { metadata }
           .hvorMinstEnForekomst {type.finnesBlant("UTLEIE_AV_FAST_EIENDOM", "UTLEIE_SEKUNDAERBOLIGMODELL") }        

         val harUtleieIFjoraaret = historiskSkattemelding(EttÅrTilbake)
             { skattemelding ->
                skattemelding
                 .hvorMinstEnForekomst {
                   (sjablongbehandletUtleie er oppgitt) eller
                   (sjablongbehandletUtleieIUtlandet er oppgitt) eller
                   (regnskapsbehandletUtleie er oppgitt) eller
                   (regnskapsbehandletUtleieIUtlandet er oppgitt) eller
                   (sjablongOgRegnskapsbehandletUtleie er oppgitt)}                   
             }

        // Kontrollen gir utslag dersom skattyter hadde utleie i fjor i Norge eller utlandet
        // og ikke har oppgitt noen utleie i år,
        // Skal ikke gi utslag hvis skattyter har fått utvidet veileding om utleie    
        skattemelding
             .utslagFor {

             val harUtleieIAar = forForekomsterAv{fastEiendom}
                 .hvorMinstEnForekomst {fastEiendomSomUtleieobjekt er oppgitt }     

             hvis(ikke(harUtvidetVeiledningPaaUtleie) og
                  harUtleieIFjoraaret og 
                  ikke(harUtleieIAar)
             )
             {
                 betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
             }
         }
    }

